/*
Some notes:
    TAA
        Uses catmull-rom for moving pixels -> produces blur for moving pixels
        The city is not actual rendered geometry, so the reprojection is incorrect -> ghosting
    Since normals are not computed from the geometry, lighting and shadows are simplified inside reflections
    Anistropic surfaces are over-denoised/blurred -> can be reduced by projecting samples on the normal plane
    Screen space normals results in false smooth edges and "noisy" normals
        A possible improvement is to search in the whole 3x3 box around the pixel
        More expensive + Tan and Bit can be parallel
    EPIC CAMERA ANIMATIONS
*/

vec4 textureCube(vec2 UV) {
    //Samples the cubemap
    float Sign = -mod(floor(UV.y*I1024),2.)*2.+1.;
    vec3 D = vec3(vec2(UV.x,mod(UV.y,1024.))*I512-1.,Sign);
    if (UV.y>4096.) D = D.xzy;
    else if (UV.y>2048.) D = D.zxy;
    return texture(iChannel3,D);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float YOffset = floor(fragCoord.x*I1024)*1024.+floor(fragCoord.y*I1024)*3072.;
    vec3 Color = textureCube(mod(fragCoord,1024.)+vec2(0.,YOffset)).xyz;


        //DEBUG (if you find it interesting)
        //Color = vec3(texture(iChannel2,fragCoord*IRES).w*0.1);
        //Color = FloatToVec3(texture(iChannel2,fragCoord*IRES).w); //Normals
            //Color = FloatToVec3(texture(iChannel0,fragCoord*IRES).z)*ReflConst; //Raw reflections
                //Color = texture(iChannel0,fragCoord*IRES).www; //Raw shadows
            //Color = texture(iChannel2,fragCoord*IRES).zzz; //Denoised shadows (B)
                //Color = FloatToVec3(texture(iChannel1,fragCoord*IRES).y)*ReflConst; //Denoised reflections (B)
        //vec2 sc = texture(iChannel2,fragCoord*IRES).xy;
        //Color = vec3(FloatToVec2(sc.x)*ReflConst,sc.y); //Buffer C Reflection light


    fragColor = vec4(pow(1.-exp(-1.2*Color),vec3(0.45)),1.);
}

//Constants
const float FOV = radians((65.)/2.);
const float ReflConst = 3.;
const float IReflConst = 1./ReflConst;
const vec3 SunLight = vec3(2.6,2.,1.);
const vec3 SunDir = normalize(vec3(1.,0.7,-0.3));
const float SunCR = 0.1;
const float PI = 3.14159265;
const float PI2 = PI*2.;
const float HPI = PI*0.5;
const float IPI = 1./PI;
const float ToRadians = PI/180.;
const float I3 = 1./3.;
const float I16 = 1./16.;
const float I26 = 1./26.;
const float I32 = 1./32.;
const float I64 = 1./64.;
const float I128 = 1./128.;
const float I255 = 1./255.;
const float I256 = 1./256.;
const float I300 = 1./300.;
const float I512 = 1./512.;
const float I1024 = 1./1024.;
const float I2048 = 1./2048.;
const float Sqrt2 = sqrt(2.);
const float ISqrt2 = sqrt(0.5);
const float Sqrt3 = sqrt(3.);
const float ISqrt3 = 1./sqrt(3.);
const vec2 eps = vec2(0.,0.001);
const float CFOV = tan(FOV);
const vec2 SSOffsets8[8] = vec2[8](vec2(0.,0.2),vec2(0.,-0.2),vec2(0.2,0.),vec2(-0.2,0.),
                                  vec2(0.4),vec2(-0.4),vec2(-0.4,0.4),vec2(0.4,-0.4));
const vec2 SSOffsets[16] = vec2[16](vec2(0.),vec2(-0.4,-0.4),vec2(0.,0.2),vec2(0.15,-0.4),vec2(-0.4,-0.15),
                                    vec2(0.15,0.4),vec2(-0.2,-0.2),vec2(-0.4,0.4),vec2(0.4,0.15),vec2(0.2,-0.2),
                                    vec2(0.4,0.4),vec2(-0.4,0.15),vec2(0.4,-0.15),
                                    vec2(-0.15,0.4),vec2(0.4,-0.4),vec2(-0.15,-0.4));
//Defines
#define RES iChannelResolution[0].xy
#define IRES 1./iChannelResolution[0].xy
#define ASPECT vec2(iChannelResolution[0].x/iChannelResolution[0].y,1.)

struct HIT { float D; vec3 DC; float M; float Specular; float Metal; };

//SDF
float DFBox(vec3 p, vec3 b) {
    vec3 d = abs(p-b*0.5)-b*0.5;
    return min(max(d.x,max(d.y,d.z)),0.)+length(max(d,0.));
}

float DFBox(vec2 p, vec2 b) {
    vec2 d = abs(p-b*0.5)-b*0.5;
    return min(max(d.x,d.y),0.)+length(max(d,0.));
}

float DFBoxC2(vec2 p, vec2 b) {
    vec2 d = abs(p)-b;
    return min(max(d.x,d.y),0.)+length(max(d,0.));
}

float DFExtrude(vec3 p, float sdf, float h) {
    //By IQ: https://www.shadertoy.com/view/4lyfzw
    vec2 w = vec2(sdf,abs(p.z)-h);
  	return min(max(w.x,w.y),0.)+length(max(w,0.));
}

float DFDisk(vec3 p) {
    float d = length(p.xz-0.5)-0.35;
    vec2 w = vec2(d,abs(p.y));
    return min(max(w.x,w.y),0.)+length(max(w,0.));
}

float DFLine(vec3 p, vec3 a, vec3 b) {
    vec3 ba = b-a;
    float k = dot(p-a,ba)/dot(ba,ba);
    return length((a+clamp(k,0.,1.)*(b-a))-p);
}

vec2 Rotate(vec2 p, float ang) {
    float c = cos(ang), s = sin(ang);
    return vec2(p.x*c-p.y*s,p.x*s+p.y*c);
}

vec2 Repeat(vec2 p, float n) {
    float ang = 2.*3.14159/n;
    float sector = floor(atan(p.x,p.y)/ang+0.5);
    p = Rotate(p,sector*ang);
    return p;
}

float smin(float a, float b, float k) {
    //https://iquilezles.org/articles/smin
    float h = max(k-abs(a-b),0.)/k;
    return min(a,b)-h*h*h*k*(1.0/6.0);
}

//Analytic
float SolidAngle(vec3 P, vec3 N, vec3 p0, vec3 p1, vec3 p2) {
    //Returns the solid angle for a visible triangle
    vec3 v0=normalize(p0-P);
    vec3 v1=normalize(p1-P);
    vec3 v2=normalize(p2-P);
    float ret=abs(dot(N,normalize(cross(v0,v1)))*acos(dot(v0,v1))+
                dot(N,normalize(cross(v1,v2)))*acos(dot(v1,v2))+
                dot(N,normalize(cross(v2,v0)))*acos(dot(v2,v0)));
    return ((isnan(ret))?0.:ret);
}

vec3 ARand23(vec2 uv) {
    //Analytic random
    return fract(sin(uv.x*uv.y)*vec3(403.125,486.125,513.432)+cos(dot(uv,vec2(13.18273,51.2134)))*vec3(173.137,261.23,203.127));
}

float ARand21(vec2 uv) {
    //Analytic random
    return fract(sin(uv.x*uv.y)*403.125+cos(dot(uv,vec2(13.18273,51.2134)))*173.137);
}

mat3 TBN(vec3 N) {
    vec3 Nb,Nt;
    if (abs(N.y)>0.999) {
        Nb = vec3(1.,0.,0.);
        Nt = vec3(0.,0.,1.);
    } else {
    	Nb = normalize(cross(N,vec3(0.,1.,0.)));
    	Nt = normalize(cross(Nb,N));
    }
    return mat3(Nb.x,Nt.x,N.x,Nb.y,Nt.y,N.y,Nb.z,Nt.z,N.z);
}

vec3 TBN(vec3 N, out vec3 O) {
    O = ((abs(N.y)>0.999)?vec3(1.,0.,0.):normalize(cross(N,vec3(0.,1.,0.))));
    return normalize(cross(O,N));
}

float boxfar(vec3 origin, vec3 dir, vec3 bmin, vec3 bmax) {
    vec3 tMin = (bmin-origin)*dir;
    vec3 tMax = (bmax-origin)*dir;
    vec3 t2 = max(tMin,tMax);
    return min(min(t2.x,t2.y),t2.z);
}

float boxfar(vec2 origin, vec2 dir, vec2 bmin, vec2 bmax) {
    vec2 tMin = (bmin-origin)*dir;
    vec2 tMax = (bmax-origin)*dir;
    vec2 t2 = max(tMin,tMax);
    return min(t2.x,t2.y);
}

vec2 box(vec3 origin, vec3 dir, vec3 bmin, vec3 bmax) {
    vec3 tMin = (bmin-origin)*dir;
    vec3 tMax = (bmax-origin)*dir;
    vec3 t1 = min(tMin,tMax);
    vec3 t2 = max(tMin,tMax);
    return vec2(max(max(t1.x,t1.y),t1.z),min(min(t2.x,t2.y),t2.z));
}

vec2 box(vec2 origin, vec2 dir, vec2 bmin, vec2 bmax) {
    vec2 tMin = (bmin-origin)*dir;
    vec2 tMax = (bmax-origin)*dir;
    vec2 t1 = min(tMin,tMax);
    vec2 t2 = max(tMin,tMax);
    return vec2(max(t1.x,t1.y),min(t2.x,t2.y));
}

vec3 boxNormal(vec3 origin, vec3 dir, vec3 bmin, vec3 bmax, out vec2 bb) {
    vec3 tMin = (bmin-origin)*dir;
    vec3 tMax = (bmax-origin)*dir;
    vec3 t1 = min(tMin,tMax);
    vec3 t2 = max(tMin,tMax);
    bb = vec2(max(max(t1.x,t1.y),t1.z),min(min(t2.x,t2.y),t2.z));
    vec3 signdir = -max(vec3(0.),sign(dir))*2.+1.;
    if (t1.x>max(t1.y,t1.z)) return vec3(signdir.x,0.,0.);
    else if (t1.y>t1.z) return vec3(0.,signdir.y,0.);
    else return vec3(0.,0.,signdir.z);
}

vec3 RandSampleCos(vec2 v) {
    float theta = sqrt(v.x);
    float phi = 2.*3.14159*v.y;
    float x = theta*cos(phi);
    float z = theta*sin(phi);
    return vec3(x,z,sqrt(max(0.,1.-v.x)));
}

vec3 RandSampleCosXYer(vec2 v) {
    float theta = sqrt(v.x);
    float phi = 2.*3.14159*v.y;
    float x = theta*cos(phi);
    float z = theta*sin(phi);
    return vec3(x,z,0.);
}

vec3 SchlickFresnel(vec3 r0, float angle) {
    //Schlick Fresnel approximation
    return r0+(1.-r0)*pow(1.-angle,5.);
}

vec3 SampleWindow(vec3 p, vec3 d, float M) {
    //Samples the window
    if (M>3.5) {
        //Render city
        float YCoeff = pow(1.-max(0.,d.y),8.);
        vec3 L = mix(vec3(0.025,0.13,0.17)*YCoeff+vec3(0.3,0.05,0.02)*YCoeff*YCoeff,SunLight,pow(max(0.,dot(d,SunDir)),24.));
        L *= float(d.y>0.);
        //City
        float R = 500.;
        float BWidth = 40.;
        float BHeight = 30.;
        for (int i=0; i<4; i++) {
            float A = dot(p.xz,p.xz)-R*R;
            float B = 2.*dot(p.xz,d.xz);
            float C = dot(d.xz,d.xz);
            float cP = B/C;
            float cQ = A/C;
            float det = cP*cP*0.25-cQ;
            float t1 = -cP*0.5+sqrt(det);
            vec3 sp = p+d*t1;
            float xcoord = atan(sp.x-2.,sp.z-2.)*R+20.;
            vec3 RandV = ARand23(vec2(floor(xcoord/BWidth)+0.5));
            if ((t1<L.x || L.y>=0.) &&
                mod(xcoord,BWidth)-BWidth*0.5<floor(BWidth*(0.2+0.2*RandV.x)*0.5)*2. &&
                sp.y<floor((-50.+BHeight*(0.5+0.5*RandV.y))*0.5)*2.) {
                L = vec3(2.6/max(1.,1.+R*0.02)*float(DFBox(mod(vec2(xcoord,sp.y),vec2(2.))-vec2(0.4,0.5),vec2(1.2,1.2))<0.
                         && ARand21(vec2(floor(xcoord*0.5),floor(sp.y*0.5))*0.25)<0.4));
            }
            R *= 0.5;
            BHeight *= 1.4;
        }
        return L;
    } else return SunLight;
}

//Float-Vec conversion
vec3 FloatToVec3(float v) {
    float x = fract(v);
    float z = floor(v*I300);
    float y = floor(v-z*300.)*I300;
    return vec3(x,y,z*I300);
}

float Vec3ToFloat(vec3 v) {
    v = min(v,vec3(0.998));
    return v.x+floor(v.y*300.)+floor(v.z*300.)*300.;
}

vec2 FloatToVec2(float v) {
    return vec2(fract(v),floor(v)*I2048);
}

float Vec2ToFloat(vec2 v) {
    v = min(v,vec2(0.999));
    return v.x+floor(v.y*2048.);
}

//Camera
const int NVecs = 11;
vec3 Positions[NVecs] = vec3[NVecs](vec3(-1.5,3.5,-1.5),
                            vec3(-1.5,0.5,-1.5),vec3(-1.5,0.5,-1.5),vec3(-1.5,0.5,-1.5), //Low position
                            vec3(1.99,2.3,3.7),vec3(1.99,2.3,3.7), //Near door
                            vec3(3.2,2.8,2.2),vec3(3.2,2.8,2.2), //Look out from window
                            vec3(3.4,2.3,-2.5),vec3(3.4,2.3,-2.5), //Beside the window
                            vec3(-1.5,3.5,-1.5)
                            );
vec3 Centers[NVecs] = vec3[NVecs](vec3(2.,1.,2.),
                          vec3(2.,0.3,2.),vec3(2.,0.3,2.),vec3(2.,1.2,4.),
                          vec3(3.,0.5,1.),vec3(4.,2.2,2.3),
                          vec3(20.,-1.,2.),vec3(20.,-1.,2.),
                          vec3(2.,1.5,2.),vec3(2.,1.5,2.),
                          vec3(2.,1.,2.)
                          );
vec3 CameraCenter(vec4 Mouse, float t) {
    if (Mouse.z>0.) {
        return vec3(2.,1.,2.);
    } else {
        //Epic animation
        float Time = t*(1.-exp(-t*0.125));
        float TPeriod = 1.;
        float ITPeriod = 1./TPeriod;
        int PIndex = min(int(floor(Time*ITPeriod)),NVecs-1);
        float fx = fract(Time*ITPeriod);
        return mix(Centers[PIndex],Centers[(PIndex+1)%NVecs],fx*fx*(3.-2.*fx));
    }
}

vec3 Position(vec4 Mouse, float t, vec2 ires) {
    vec2 Angles = vec2(2.8,0.01)+Mouse.xy*ires*vec2(2.3,1.2);
    if (Mouse.z>0.) {
        return vec3(2.,2.,2.)+vec3(vec2(sin(Angles.x),cos(Angles.x))*cos(Angles.y),sin(Angles.y)).xzy*8.*(0.125*5.);
    } else {
        //Epic animation
        float Time = t*(1.-exp(-t*0.125));
        float TPeriod = 1.;
        float ITPeriod = 1./TPeriod;
        int PIndex = min(int(floor(Time*ITPeriod)),NVecs-1);
        float fx = fract(Time*ITPeriod);
        return mix(Positions[PIndex],Positions[(PIndex+1)%NVecs],fx*fx*(3.-2.*fx));
    }
}

//SDF
void Trace_WindowFrame(vec3 P, vec3 D, inout HIT OUT) {
    float dft = 0.; float tmpt,aplen; vec3 ap,rp;
    for (int i=0; i<128; i++) {
        //SDF
        ap = P+D*dft;
        ap.yz = abs(ap.yz); //Symmetry
        //Base pillars (idk)
        tmpt = max(DFBox(ap-vec3(-0.1,0.,-0.1),vec3(0.1,1.9,0.2)),-length(ap.xz-vec2(-0.125,0.1))+0.075);
        tmpt = min(tmpt,max(DFBox(ap-vec3(-0.1,-0.1,0.),vec3(0.1,0.2,1.9)),-length(ap.xy-vec2(-0.125,0.1))+0.075));
        //Small pillar
        rp = vec3(ap.x,Rotate(ap.yz,0.78539816339));
        tmpt = min(tmpt,max(max(DFBox(rp-vec3(-0.1,-0.1,1.),vec3(0.1,0.2,0.9)),-length(rp.xy-vec2(-0.125,0.1))+0.075),
                    -length(rp.xy-vec2(-0.125,-0.1))+0.075));
        //Donuts
        aplen = length(ap.yz);
        tmpt = min(tmpt,max(max(length(vec2(ap.x,aplen-1.))-0.1,-length(vec2(ap.x+0.125,aplen-1.1))+0.075),
                   -length(vec2(ap.x+0.125,aplen-0.9))+0.075));
        tmpt = min(tmpt,max(max(length(vec2(ap.x,aplen-1.9))-0.1,-length(vec2(ap.x+0.125,aplen-2.))+0.075),
                   -length(vec2(ap.x+0.125,aplen-1.8))+0.075));
        //Check intersection
        dft += tmpt;
        if (dft>OUT.D) break;
        if (tmpt<0.001) {
            OUT = HIT(dft,vec3(0.5,0.2,0.1),0.2,0.25,0.95);
            break;
        }
    }
}

void Trace_MetalCylinders(vec3 P, vec3 D, inout HIT OUT) {
    //First vertical cylinder
    vec3 RP = P-vec3(3.5,0.,0.);
    float A = dot(RP.xz,RP.xz)-0.0289; //R = 0.17
    float B = 2.*dot(RP.xz,D.xz);
    float C = dot(D.xz,D.xz);
    float cP = B/C;
    float cQ = A/C;
    float det = cP*cP*0.25-cQ;
    if (det>=0.) {
        //Valid intersection
        det = sqrt(det);
        float t0 = -cP*0.5-det;
        float t1 = -cP*0.5+det;
        vec3 sp = P+D*t0;
        if (t0>0. && t0<OUT.D && sp.y<3.4) {
            OUT = HIT(t0,vec3(1.),0.025,1.,1.);
        }
    }
    //Bent cylinder
    float dft = 0.; float tmpt,aplen; vec3 ap,rp;
    for (int i=0; i<128; i++) {
        //SDF
        ap = P+D*dft;
        aplen = length(ap.xy-vec2(3.1,3.4));
        tmpt = max(max(length(vec2(ap.z,aplen-0.4))-0.17,-ap.y+3.4),-ap.x+3.1);
        aplen = length(ap.xz-vec2(1.5,0.4));
        tmpt = min(tmpt,max(max(length(vec2(ap.y-3.8,aplen-0.4))-0.17,ap.x-1.5),-ap.z-0.25));
        //Check intersection
        dft += tmpt;
        if (dft>OUT.D) break;
        if (tmpt<0.001) {
            OUT = HIT(dft,vec3(1.),0.025,1.,1.);
            break;
        }
    }
    //Horisontal cylinder
    RP = P-vec3(3.5,3.8,0.);
    A = dot(RP.yz,RP.yz)-0.0289; //R = 0.18
    B = 2.*dot(RP.yz,D.yz);
    C = dot(D.yz,D.yz);
    cP = B/C;
    cQ = A/C;
    det = cP*cP*0.25-cQ;
    if (det>=0.) {
        //Valid intersection
        det = sqrt(det);
        float t0 = -cP*0.5-det;
        float t1 = -cP*0.5+det;
        vec3 sp = P+D*t0;
        if (t0>0. && t0<OUT.D && sp.x<=3.11 && sp.x>=1.5) {
            OUT = HIT(t0,vec3(1.),0.025,1.,1.);
        }
    }
}

void Trace_RoundBox(vec3 P, vec3 D, vec3 Size, float R, inout HIT OUT) {
    float dft = 0.; float tmpt;
    for (int i=0; i<128; i++) {
        //SDF
        tmpt = DFBox(P+D*dft,Size)-R;
        //Check intersection
        dft += tmpt;
        if (dft>OUT.D) break;
        if (tmpt<0.001) {
            OUT = HIT(dft,vec3(0.47,0.35,0.25),0.3,0.05,0.6);
            break;
        }
    }
}

void Trace_Cylinder0(vec3 P, vec3 D, inout HIT OUT) {
    float A = dot(P.xz,P.xz)-0.04;
    float B = 2.*dot(P.xz,D.xz);
    float C = dot(D.xz,D.xz);
    float cP = B/C;
    float cQ = A/C;
    float det = cP*cP*0.25-cQ;
    if (det>=0.) {
        //Valid intersection
        det = sqrt(det);
        float t0 = -cP*0.5-det;
        float t1 = -cP*0.5+det;
        vec3 sp = P+D*t0;
        if (t0>0. && t0<OUT.D && abs(sp.y-(0.05+(sin(atan(sp.x,sp.z)*4.+0.1)*0.5+0.5)*0.05))<0.1) {
            OUT = HIT(t0,vec3(1.),0.,0.25,0.);
        } else if (t1>0.) {
            sp = P+D*t1;
            if (t1<OUT.D && abs(sp.y-(0.05+(sin(atan(sp.x,sp.z)*4.+0.1)*0.5+0.5)*0.05))<0.1) {
                OUT = HIT(t1,vec3(1.),0.,0.25,0.);
            }
        }
    }
}

void Trace_Door(vec3 P, vec3 D, inout HIT OUT) {
    //Wood door
    float dft = 0.; float tmpt; vec3 sp;
    for (int i=0; i<128; i++) {
        //SDF
        sp = P+D*dft;
        tmpt = DFBox(sp-vec3(0.01),vec3(0.88,2.08,0.03))-0.01;
        //Higher carving
        tmpt = min(max(tmpt,-DFBox(sp-vec3(0.2,1.1,-0.01),vec3(0.5,0.8,0.))+0.05),
                   DFBox(sp-vec3(0.3,1.2,0.04),vec3(0.3,0.6,0.))-0.01);
        tmpt = min(max(tmpt,-DFBox(sp-vec3(0.2,0.15,-0.01),vec3(0.5,0.6,0.))+0.05),
                   DFBox(sp-vec3(0.3,0.25,0.04),vec3(0.3,0.4,0.))-0.01);
        //Check intersection
        dft += tmpt;
        if (dft>OUT.D) break;
        if (tmpt<0.001) {
            OUT = HIT(dft,vec3(0.9),0.2,0.1,0.3);
            break;
        }
    }
    //Handle
    dft = 0.;
    for (int i=0; i<128; i++) {
        //SDF
        tmpt = DFLine(P+D*dft,vec3(0.88,0.9,-0.04),vec3(0.78,0.9,-0.04))-0.02;
        tmpt = min(tmpt,DFLine(P+D*dft,vec3(0.88,0.9,-0.04),vec3(0.88,0.9,0.))-0.02);
        //Check intersection
        dft += tmpt;
        if (dft>OUT.D) break;
        if (tmpt<0.001) {
            OUT = HIT(dft,vec3(0.9,0.7,0.2),0.03,0.8,0.9);
            break;
        }
    }
}

void Trace_DoorFrame(vec3 P, vec3 D, inout HIT OUT) {
    //Wood door
    float dft = 0.; float tmpt; vec3 sp;
    for (int i=0; i<128; i++) {
        //SDF
        sp = P+D*dft;
        tmpt = max(DFBox(sp-vec3(-0.15,0.,-0.08),vec3(0.15,2.25,0.08)),
                   -DFLine(sp,vec3(-0.15,0.,-0.12),vec3(-0.15,2.25,-0.12))+0.1);
        tmpt = min(tmpt,max(DFBox(sp-vec3(0.9,0.,-0.08),vec3(0.15,2.25,0.08)),
                   -DFLine(sp,vec3(1.05,0.,-0.12),vec3(1.05,2.25,-0.12))+0.1));
        tmpt = max(min(tmpt,DFBox(sp-vec3(0.,2.1,-0.08),vec3(0.9,0.15,0.08))),
                   -DFLine(sp,vec3(-0.15,2.25,-0.12),vec3(1.05,2.25,-0.12))+0.1);
        //Check intersection
        dft += tmpt;
        if (dft>OUT.D) break;
        if (tmpt<0.001) {
            OUT = HIT(dft,vec3(0.2,0.1,0.04),0.2,0.1,0.5);
            break;
        }
    }
}

void Trace_Sphere(vec3 P, vec3 D, inout HIT OUT) {
    //Wood door
    float dft = 0.; float tmpt;
    for (int i=0; i<128; i++) {
        //SDF
        tmpt = length(P+D*dft-vec3(3.1,0.5,1.))-0.5;
        //Check intersection
        dft += tmpt;
        if (dft>OUT.D) break;
        if (tmpt<0.001) {
            OUT = HIT(dft,vec3(1.),0.04,1.,1.);
            break;
        }
    }
}

//Trace function
HIT Trace(vec3 P, vec3 D, float Time) {
    HIT OUT = HIT(1000000000.,vec3(1.),-1.,-1.,-1.);
    vec3 ID = 1./D;
    //Ground and floor
    if (D.y<0.) {
        float GDist = -(P.y+0.1)/D.y;
        OUT = HIT(GDist,vec3(0.2),0.15,0.6,1.);
    }
    vec2 bb = box(P,ID,vec3(0.,-0.1,0.),vec3(4.,0.,4.)); //Floor
    if (bb.x>0. && bb.y>bb.x && bb.x<OUT.D) OUT = HIT(bb.x,vec3(0.2,0.1,0.04),0.2,0.1,0.1);

    //X-Normal wall
    vec2 bb2 = box(P,ID,vec3(3.5,-0.1,0.),vec3(4.1,4.,4.));
    if (bb2.x>0. && bb2.y>bb2.x && bb2.x<OUT.D || DFBox(P-vec3(3.5,-0.1,0.),vec3(0.6,4.1,4.))<=0.) {
        //Wall
        bb = box(P,ID,vec3(4.,-0.1,0.),vec3(4.1,4.,4.));
        if (bb.x>0. && bb.y>bb.x && bb.x<OUT.D) {
            OUT = HIT(bb.x,vec3(1.),0.1,0.75,0.05);
            //Emissive circle
            float CLen = length(P.zy+D.zy*bb.x-2.);
            float CX = atan(P.z+D.z*bb.x-2.,P.y+D.y*bb.x-2.);
            if (CLen<1.9 && P.x+D.x*bb.x<4.05) {
                OUT.M = 3.;
                //Broken windows
                if ((CX>0. && CX<1.5707963 && CLen<0.9) ||
                    (CX>2.35619449 && CLen>0.9)) OUT.M = 4.5;
            }
        }
        //Window frame
        Trace_WindowFrame(P-vec3(4.,2.,2.),D,OUT);
    }

    //Z-Normal wall
    bb2 = box(P,ID,vec3(0.,-0.1,3.6),vec3(4.,4.,4.1));
    if (bb2.x>0. && bb2.y>bb2.x && bb2.x<OUT.D || DFBox(P-vec3(0.,-0.1,3.6),vec3(4.,4.1,0.5))<=0.) {
        //Wall
        bb = box(P,ID,vec3(0.,-0.1,4.),vec3(4.,4.,4.1));
        if (bb.x>0. && bb.y>bb.x && bb.x<OUT.D) OUT = OUT = HIT(bb.x,vec3(1.,0.1,0.1),0.3,0.5,0.2);
        //Metal cylinders
        Trace_MetalCylinders(P-vec3(0.,-0.15,3.8),D,OUT);
        Trace_MetalCylinders(P-vec3(-0.8,-0.65,3.8),D,OUT);
        //Door
        Trace_Door(P-vec3(1.2,0.,3.95),D,OUT);
        Trace_DoorFrame(P-vec3(1.2,0.,4.),D,OUT);
        //Leaning wood
        vec3 RP = P-vec3(0.1,0.02,3.7); RP.yz = Rotate(RP.yz,-0.08);
        vec3 RD = D; RD.yz = Rotate(RD.yz,-0.08);
        bb = box(RP,1./RD,vec3(0.),vec3(0.24,2.8,0.06));
        if (bb.x>0. && bb.y>bb.x && bb.x<OUT.D) OUT = HIT(bb.x,vec3(0.6,0.3,0.09),0.2,0.1,0.05);
        RP = RP+vec3(0.08,0.06,0.06);
        bb = box(RP,1./RD,vec3(0.),vec3(0.24,2.8,0.06));
        if (bb.x>0. && bb.y>bb.x && bb.x<OUT.D) OUT = HIT(bb.x,vec3(0.6,0.3,0.09),0.2,0.1,0.05);
        RP = RP+vec3(-0.1,0.06,0.06);
        bb = box(RP,1./RD,vec3(0.),vec3(0.24,2.8,0.06));
        if (bb.x>0. && bb.y>bb.x && bb.x<OUT.D) OUT = HIT(bb.x,vec3(0.6,0.3,0.09),0.2,0.1,0.05);
    }

    //Boxes close to vec3(4,0,4)
    bb2 = box(P,ID,vec3(2.,0.,1.5),vec3(4.,3.,4.));
    if (bb2.x>0. && bb2.y>bb2.x && bb2.x<OUT.D || DFBox(P-vec3(2.,0.,1.5),vec3(2.,3.,2.5))<=0.) {
        //Boxes
        vec3 RP = P-vec3(3.1,0.,3.); RP.xz = Rotate(RP.xz,0.9);
        vec3 RD = D; RD.xz = Rotate(RD.xz,0.9);
        Trace_RoundBox(RP,RD,vec3(0.45),0.05,OUT);
        RP = P-vec3(2.45,0.05,3.); RP.xz = Rotate(RP.xz,-0.1);
        RD = D; RD.xz = Rotate(RD.xz,-0.1);
        Trace_RoundBox(RP,RD,vec3(0.45,0.55,0.45),0.05,OUT);
        //Rotated box
        RP = P-vec3(2.4,0.3,2.12); RP.xz = Rotate(RP.xz,0.3); RP.zy = Rotate(RP.zy,0.8);
        RD = D; RD.xz = Rotate(RD.xz,0.3); RD.zy = Rotate(RD.zy,0.8);
        Trace_RoundBox(RP,RD,vec3(0.65,1.2,0.35),0.05,OUT);
    }

    //Sphere
    Trace_Sphere(P,D,OUT);

    //Rotating block
    float TAngle = Time;
    vec3 RP = P-vec3(-1.,-0.1,3.); RP.xz = Rotate(RP.xz,TAngle);
    vec3 RD = D; RD.xz = Rotate(RD.xz,TAngle);
    bb = box(RP,1./RD,vec3(-0.8,0.,-0.05),vec3(0.8,0.8,0.1));
    if (bb.x>0. && bb.y>bb.x && bb.x<OUT.D) OUT = HIT(bb.x,vec3(0.),0.1,0.5,0.);

    //Return
    return OUT;
}

//Secondary rays

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 Output = vec4(0.);
    if (DFBox(fragCoord-3.,RES-6.)<0.) {
        //
        //CURRENT FRAME: secondary rays
        //
        float CurrentFrame = float(iFrame-1);
        vec2 SSOffset = SSOffsets[(iFrame-1)%16];
        float CurrentTime = texture(iChannel1,vec2(0.5,0.5)*IRES).x;
        vec4 CurrentMouse = texture(iChannel1,vec2(1.5,0.5)*IRES);
        vec3 Pos = Position(CurrentMouse,CurrentTime,IRES);
        vec3 Eye = normalize(CameraCenter(CurrentMouse,CurrentTime)-Pos);
        vec3 Dir = normalize(vec3(((fragCoord+SSOffset)*IRES*2.-1.)*CFOV*ASPECT,1.)*TBN(Eye));
        float Distance = texture(iChannel2,fragCoord*IRES).w;
        if (Distance>-0.5) {
            //Geometry
            HIT Pixel = Trace(Pos+Dir*(Distance-0.01),Dir,CurrentTime); //Get attributes
            vec3 Normal = normalize(FloatToVec3(texture(iChannel3,fragCoord*IRES).w)*2.-1.);
            vec3 PPos = Pos+Dir*Distance+Normal*0.002;
            vec3 RandV = ARand23(fragCoord*IRES*(1.+mod(CurrentFrame*7.253,9.234)));
            vec4 REF = vec4(0.,0.,0.,-1.);
            vec2 SHAD = vec2(0.,-1.);
            if (Pixel.M<2.5) {
                //Reflections
                vec3 RefDir = reflect(Dir,Normal);
                float RefCR = Pixel.M*min(1.,tan((HPI-acos(dot(RefDir,Normal)))));
                vec3 RefSample = normalize(RandSampleCos(RandV.xy)*TBN(RefDir)*RefCR+RefDir);
                HIT RefHit = Trace(PPos,RefSample,CurrentTime);
                if (RefHit.M>-0.5) {
                    //Geometry
                    REF.w = RefHit.D;
                    if (RefHit.M>2.5) {
                        REF.xyz = SampleWindow(PPos,RefSample,RefHit.M);
                    } else if (PPos.x+RefSample.x*RefHit.D<3.99) {
                        //Direct
                        vec3 sPPos = PPos+RefSample*(RefHit.D-0.01);
                        if (Trace(sPPos,SunDir,CurrentTime).M>2.5)
                            REF.xyz = SunLight*RefHit.DC*(dot(-RefSample,SunDir)*0.5+0.5);
                        //Indirect
                        sPPos = PPos+RefSample*RefHit.D;
                        vec3 IL = vec3(0.);
                        if (!(sPPos.y<0.001 && -RefSample.y>0.99)) {
                            IL += vec3(0.2,0.1,0.04)*1.5*
                                    (SolidAngle(sPPos,-RefSample,vec3(4.,0.,2.),vec3(4.,0.,1.),vec3(2.,0.,2.))+
                                    SolidAngle(sPPos,-RefSample,vec3(1.8,0.,1.),vec3(0.,0.,1.2),vec3(0.,0.,4.))+
                                    SolidAngle(sPPos,-RefSample,vec3(2.,0.,4.),vec3(0.,0.,1.2),vec3(0.,0.,4.))
                                    );
                        }
                        if (!(sPPos.z>3.999 && -RefSample.z<-0.99)) {
                            IL += vec3(1.,0.2,0.2)*0.7*
                                    (SolidAngle(sPPos,-RefSample,vec3(1.,0.,4.),vec3(0.,0.,4.),vec3(1.,1.5,4.)));
                        }
                        REF.xyz += IL/(2.*PI);
                    }
                } else {
                    //Sky
                    REF = vec4(0.,0.,0.,100000.);
                }
                //Shadows
                vec3 ShadSample = normalize(RandSampleCos(RandV.xy)*TBN(SunDir)*SunCR+SunDir);
                if (dot(ShadSample,Normal)>0. && PPos.x<4.01) {
                    HIT ShadHit = Trace(PPos,ShadSample,CurrentTime);
                    SHAD.y = ShadHit.D;
                    if (ShadHit.M>2.5) SHAD.x = 1.;
                }
            }
            //Output
            Output = vec4(REF.w,SHAD.y,Vec3ToFloat(REF.xyz*IReflConst),SHAD.x);
        } else {
            //Sky
            Output = vec4(-1.,-1.,0.,0.);
        }
    }
    fragColor = Output;
}

//Copy depth + Wavelet iteration 1 + Attributes

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 Output = vec4(0.);
    if (DFBox(fragCoord-3.,RES-6.)<0.) {
        //
        //PRIOR FRAME: copy depth
        //
        Output.w = texture(iChannel2,fragCoord*IRES).w; //Copy depth prior frame
        Output.x = texture(iChannel3,fragCoord*IRES).w; //Copy normal




        //
        //CURRENT FRAME: wavelet iteration 1
        //
        float CShadow = 0.;
        float ShadW = 1.;
        float CurrentFrame = float(iFrame-1);
        vec2 SSOffset = SSOffsets[(iFrame-1)%16];
        float CurrentTime = texture(iChannel1,vec2(0.5,0.5)*IRES).x;
        vec4 CurrentMouse = texture(iChannel1,vec2(1.5,0.5)*IRES);
        vec3 Pos = Position(CurrentMouse,CurrentTime,IRES);
        vec3 Eye = normalize(CameraCenter(CurrentMouse,CurrentTime)-Pos);
        vec3 Tan; vec3 Bit = TBN(Eye,Tan);
        mat3 EyeMat = TBN(Eye);
        vec3 Dir = normalize(vec3(((fragCoord+SSOffset)*IRES*2.-1.)*CFOV*ASPECT,1.)*EyeMat);
        float Distance = texture(iChannel2,fragCoord*IRES).w;
        if (Distance>-0.5) {
            //Geometry
            HIT Pixel = Trace(Pos+Dir*(Distance-0.01),Dir,CurrentTime); //Get attributes
            vec3 Normal = normalize(FloatToVec3(texture(iChannel3,fragCoord*IRES).w)*2.-1.);
            vec3 PPos = Pos+Dir*Distance;
            vec4 RefShad = texture(iChannel0,fragCoord*IRES);
            vec2 RandV = ARand23(fragCoord*IRES*(1.+mod(CurrentFrame*7.253,9.234))).xy;
            //Denoisers
            if (Pixel.M<1.) {
                //Non-emissive material
                //Reflections denoiser
                vec3 RefDir = reflect(Dir,Normal);
                float RefCR = Pixel.M*min(1.,tan((HPI-acos(dot(RefDir,Normal)))));
                vec3 Light = FloatToVec3(RefShad.z)*ReflConst*2.;
                float W = 2.;
                vec3 CVPos0 = vec3(RefShad.x*RefCR,0.,Distance+RefShad.x)*TBN(Dir);
                vec3 CVPos1 = vec3(-RefShad.x*RefCR,0.,Distance+RefShad.x)*TBN(Dir);
                vec3 LVPos0 = vec3(dot(CVPos0,Tan),dot(CVPos0,Bit),dot(CVPos0,Eye));
                vec3 LVPos1 = vec3(dot(CVPos1,Tan),dot(CVPos1,Bit),dot(CVPos1,Eye));
                vec2 Luv0 = ((LVPos0.xy/LVPos0.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                vec2 Luv1 = ((LVPos1.xy/LVPos1.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                float MaxRadius = min(32.,length(Luv0-Luv1)*0.5);
                for (float x=-2.; x<2.5; x+=1.) {
                    for (float y=-2.; y<2.5; y+=1.) {
                        if (x==0. && y==0.) continue;
                        vec2 Offset2 = normalize(vec2(x,y))*max(abs(x),abs(y))*(0.5*MaxRadius);
                        vec2 SUV = floor(fragCoord+Offset2)+0.5;
                        float SDistance = texture(iChannel2,SUV*IRES).w;
                        vec3 SDir = normalize(vec3(((SUV+SSOffset)*IRES*2.-1.)*(ASPECT*CFOV),1.)*EyeMat);
                        if (SDistance<-0.5 || abs(dot(Pos+SDir*SDistance-PPos,Normal))>0.1 || DFBox(SUV-3.,RES-6.)>0.) continue;
                        vec3 SNor = FloatToVec3(texture(iChannel3,SUV*IRES).w)*2.-1.;
                        //Reflection direction
                        vec4 SRefShad = texture(iChannel0,SUV*IRES);
                        if (SRefShad.x<-0.5) continue;
                        vec2 SRand = ARand23(SUV*IRES*(1.+mod(CurrentFrame*7.253,9.234))).xy;
                        vec3 SRefDir = reflect(SDir,SNor);
                        float SCR = Pixel.M*min(1.,tan((HPI-acos(dot(RefDir,Normal))))); //Måste ray-tracea för Hit.y annars
                        vec3 SRDir = normalize(RandSampleCos(SRand)*TBN(SRefDir)*SCR+SRefDir);
                        vec3 HitP = Pos+SDir*SDistance+SNor*0.0015+SRDir*SRefShad.x;
                        if (dot(HitP-PPos,Normal)<=0.) continue;
                        if (sqrt(1./dot(normalize(HitP-PPos),RefDir)-1.)<=RefCR) {
                            Light += FloatToVec3(SRefShad.z)*ReflConst;
                            W += 1.;
                        }
                    }
                }
                Light = Light/W;
                Output.y = Vec3ToFloat(Light*IReflConst);
                //Shadow denoiser
                CVPos0 = vec3(RefShad.y*SunCR,0.,Distance)*TBN(Dir);
                CVPos1 = vec3(-RefShad.y*SunCR,0.,Distance)*TBN(Dir);
                LVPos0 = vec3(dot(CVPos0,Tan),dot(CVPos0,Bit),dot(CVPos0,Eye));
                LVPos1 = vec3(dot(CVPos1,Tan),dot(CVPos1,Bit),dot(CVPos1,Eye));
                Luv0 = ((LVPos0.xy/LVPos0.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                Luv1 = ((LVPos1.xy/LVPos1.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                MaxRadius = min(32.,length(Luv0-Luv1)*0.5);
                CShadow = RefShad.w*2.;
                ShadW = 2.;
                for (float x=-2.; x<2.5; x+=1.) {
                    for (float y=-2.; y<2.5; y+=1.) {
                        if (x==0. && y==0.) continue;
                        vec2 Offset2 = normalize(vec2(x,y))*max(abs(x),abs(y))*(MaxRadius*0.5);
                        vec2 SUV = floor(fragCoord+Offset2)+0.5;
                        float SDistance = texture(iChannel2,SUV*IRES).w;
                        vec3 SDir = normalize(vec3(((SUV+SSOffset)*IRES*2.-1.)*(ASPECT*CFOV),1.)*EyeMat);
                        if (SDistance<-0.5 || abs(dot(Pos+SDir*SDistance-PPos,Normal))>0.1 || DFBox(SUV-3.,RES-6.)>0.) continue;
                        vec4 SRefShad = texture(iChannel0,SUV*IRES);
                        if (SRefShad.y<-0.5) continue;
                        vec2 SRand = ARand23(SUV*IRES*(1.+mod(CurrentFrame*7.253,9.234))).xy;
                        vec3 HitP = Pos+SDir*SDistance+normalize(RandSampleCos(SRand.xy)*TBN(SunDir)*SunCR+SunDir)*SRefShad.y;
                        if (dot(HitP-PPos,Normal)<=0.) continue;
                        CShadow += SRefShad.w;
                        ShadW += 1.;
                    }
                }
                Output.z = CShadow/ShadW;
            }
        } else {
            //Sky
        }
    } else {
        //Boundary: attributes
        if (fragCoord.y<1.) {
            if (fragCoord.x<1.) Output = vec4(iTime,texture(iChannel1,vec2(0.5)*IRES).xy,0.); //Prior frame time, time
            else if (fragCoord.x<2.) Output = iMouse; //Mouse
            else if (fragCoord.x<3.) Output = texture(iChannel1,vec2(1.5,0.5)*IRES); //Prior frame mouse
            else if (fragCoord.x<4.) Output = texture(iChannel1,vec2(2.5,0.5)*IRES); //Prior prior frame mouse

        }
    }
    fragColor = Output;
}

//Primary rays + Wavelet iteration 2

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 Output = vec4(0.);
    if (DFBox(fragCoord-3.,RES-6.)<0.) {
        //
        //PRIOR FRAME: primary rays
        //
        vec2 SSOffset = SSOffsets[(iFrame)%16];
        float CurrentTime = iTime;
        vec3 Pos = Position(iMouse,CurrentTime,IRES);
        vec3 Eye = normalize(CameraCenter(iMouse,iTime)-Pos);
        vec3 Dir = normalize(vec3(((fragCoord+SSOffset)*IRES*2.-1.)*CFOV*ASPECT,1.)*TBN(Eye));
        HIT Pixel = Trace(Pos,Dir,CurrentTime);
        Output.w = ((Pixel.M<0.)?-1.:Pixel.D);




        //
        //CURRENT FRAME: wavelet iteration 2
        //
        float CurrentFrame = float(iFrame-1);
        SSOffset = SSOffsets[(iFrame-1)%16];
        CurrentTime = texture(iChannel1,vec2(0.5,0.5)*IRES).y;
        vec4 CurrentMouse = texture(iChannel1,vec2(2.5,0.5)*IRES);
        Pos = Position(CurrentMouse,CurrentTime,IRES);
        Eye = normalize(CameraCenter(CurrentMouse,CurrentTime)-Pos);
        vec3 Tan; vec3 Bit = TBN(Eye,Tan);
        mat3 EyeMat = TBN(Eye);
        Dir = normalize(vec3(((fragCoord+SSOffset)*IRES*2.-1.)*CFOV*ASPECT,1.)*EyeMat);
        float Distance = texture(iChannel1,fragCoord*IRES).w;
        if (Distance>-0.5) {
            //Geometry
            HIT Pixel = Trace(Pos+Dir*(Distance-0.01),Dir,CurrentTime); //Get attributes
            vec3 Normal = normalize(FloatToVec3(texture(iChannel3,fragCoord*IRES).w)*2.-1.);
            vec3 PPos = Pos+Dir*Distance;
            vec4 RefShad = texture(iChannel0,fragCoord*IRES);
            vec2 RandV = ARand23(fragCoord*IRES*(1.+mod(CurrentFrame*7.253,9.234))).xy;
            //Denoisers
            if (Pixel.M<1.) {
                //Non-emissive material
                //Reflections denoiser
                vec3 RefDir = reflect(Dir,Normal);
                float RefCR = Pixel.M*min(1.,tan((HPI-acos(dot(RefDir,Normal)))));
                vec3 Light = FloatToVec3(texture(iChannel1,fragCoord*IRES).y)*ReflConst*2.;
                float W = 2.;
                vec3 CVPos0 = vec3(RefShad.x*RefCR,0.,Distance+RefShad.x)*TBN(Dir);
                vec3 CVPos1 = vec3(-RefShad.x*RefCR,0.,Distance+RefShad.x)*TBN(Dir);
                vec3 LVPos0 = vec3(dot(CVPos0,Tan),dot(CVPos0,Bit),dot(CVPos0,Eye));
                vec3 LVPos1 = vec3(dot(CVPos1,Tan),dot(CVPos1,Bit),dot(CVPos1,Eye));
                vec2 Luv0 = ((LVPos0.xy/LVPos0.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                vec2 Luv1 = ((LVPos1.xy/LVPos1.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                float MaxRadius = min(8.,length(Luv0-Luv1)*0.5);
                for (float x=-2.; x<2.5; x+=1.) {
                    for (float y=-2.; y<2.5; y+=1.) {
                        if (x==0. && y==0.) continue;
                        vec2 Offset2 = normalize(vec2(x,y))*max(abs(x),abs(y))*(MaxRadius*0.5);
                        vec2 SUV = floor(fragCoord+Offset2)+0.5;
                        float SDist = texture(iChannel1,SUV*IRES).w;
                        vec3 SDir = normalize(vec3(((SUV+SSOffset)*IRES*2.-1.)*(ASPECT*CFOV),1.)*EyeMat);
                        if (SDist<-0.5 || abs(dot(Pos+SDir*SDist-PPos,Normal))>0.1 || DFBox(SUV-3.,RES-6.)>0.) continue;
                        vec3 SNor = FloatToVec3(texture(iChannel3,SUV*IRES).w)*2.-1.;
                        //Reflection direction
                        vec4 SRefShad = texture(iChannel0,SUV*IRES);
                        if (SRefShad.x<-0.5) continue;
                        vec2 SRand = ARand23(SUV*IRES*(1.+mod(CurrentFrame*7.253,9.234))).xy;
                        vec3 SRefDir = reflect(SDir,SNor);
                        float SCR = Pixel.M*min(1.,tan((HPI-acos(dot(RefDir,Normal))))); //Måste ray-tracea för Hit.y annars
                        vec3 SRDir = normalize(RandSampleCos(SRand)*TBN(SRefDir)*SCR+SRefDir);
                        vec3 HitP = Pos+SDir*SDist+SNor*0.0015+SRDir*SRefShad.x;
                        if (dot(HitP-PPos,Normal)<=0.) continue;
                        if (sqrt(1./dot(normalize(HitP-PPos),RefDir)-1.)<=RefCR) {
                            Light += FloatToVec3(texture(iChannel1,SUV*IRES).y)*ReflConst;
                            W += 1.;
                        }
                    }
                }
                Light = Light/W;
                Output.xy = vec2(Vec2ToFloat(Light.xy*IReflConst),Light.z);
                //Shadow denoiser
                CVPos0 = vec3(RefShad.y*SunCR,0.,Distance+RefShad.y)*TBN(Dir);
                CVPos1 = vec3(-RefShad.y*SunCR,0.,Distance+RefShad.y)*TBN(Dir);
                LVPos0 = vec3(dot(CVPos0,Tan),dot(CVPos0,Bit),dot(CVPos0,Eye));
                LVPos1 = vec3(dot(CVPos1,Tan),dot(CVPos1,Bit),dot(CVPos1,Eye));
                Luv0 = ((LVPos0.xy/LVPos0.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                Luv1 = ((LVPos1.xy/LVPos1.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                MaxRadius = min(8.,length(Luv0-Luv1)*0.5);
                float CShadow = texture(iChannel1,fragCoord*IRES).z*2.;
                float ShadW = 2.;
                for (float x=-2.; x<2.5; x+=1.) {
                    for (float y=-2.; y<2.5; y+=1.) {
                        if (x==0. && y==0.) continue;
                        vec2 Offset2 = normalize(vec2(x,y))*max(abs(x),abs(y))*(MaxRadius*0.5);
                        vec2 SUV = floor(fragCoord+Offset2)+0.5;
                        vec4 SRefShad = texture(iChannel1,SUV*IRES);
                        float SDistance = SRefShad.w;
                        vec3 SDir = normalize(vec3(((SUV+SSOffset)*IRES*2.-1.)*(ASPECT*CFOV),1.)*EyeMat);
                        if (SDistance<-0.5 || abs(dot(Pos+SDir*SDistance-PPos,Normal))>0.1 || DFBox(SUV-3.,RES-6.)>0.) continue;
                        float RayDist = texture(iChannel0,SUV*IRES).y;
                        if (RayDist<-0.5) continue;
                        vec2 SRand = ARand23(SUV*IRES*(1.+mod(CurrentFrame*7.253,9.234))).xy;
                        vec3 HitP = Pos+SDir*SDistance+normalize(RandSampleCos(SRand.xy)*TBN(SunDir)*SunCR+SunDir)*RayDist;
                        if (dot(HitP-PPos,Normal)<=0.) continue;
                        CShadow += SRefShad.z;
                        ShadW += 1.;
                    }
                }
                Output.z = CShadow/ShadW;
            }
        } else {
            //Sky
        }
    }
    fragColor = Output;
}

//Normals + Temporal accumulation + Composition

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 Output = vec4(0.);
    if (DFBox(fragCoord-3.,RES-6.)<0.) {
        //
        //PRIOR FRAME: normals
        //
        vec2 SSOffset = SSOffsets[(iFrame)%16];
        float CurrentTime = iTime;
        vec3 Pos = Position(iMouse,CurrentTime,IRES);
        vec3 Eye = normalize(CameraCenter(iMouse,iTime)-Pos);
        vec3 VDir = normalize(vec3(((fragCoord+SSOffset)*IRES*2.-1.)*CFOV*ASPECT,1.));
        mat3 EyeMat = TBN(Eye);
        vec3 Dir = VDir*EyeMat;
        //Compute normal
        vec3 PixelVP = VDir*texture(iChannel2,fragCoord*IRES).w;
        vec3 Tan = vec3(0.,0.,0.);
        vec3 Bit = vec3(0.,0.,0.);
        vec2 XD = vec2(texture(iChannel2,(fragCoord+vec2(1.,0.))*IRES).w,texture(iChannel2,(fragCoord+vec2(-1.,0.))*IRES).w);
        vec3 XP0 = normalize(vec3(((fragCoord+vec2(1.,0.)+SSOffset)*IRES*2.-1.)*(ASPECT*CFOV),1.))*XD.x;
        vec3 XP1 = normalize(vec3(((fragCoord-vec2(1.,0.)+SSOffset)*IRES*2.-1.)*(ASPECT*CFOV),1.))*XD.y;
        if (XD.x>-0.5) Tan = XP0-PixelVP;
        if (XD.y>-0.5 && (XD.x<-0.5 || length(Tan)>length(XP1-PixelVP))) Tan = XP1-PixelVP;
        vec2 YD = vec2(texture(iChannel2,(fragCoord+vec2(0.,1.))*IRES).w,texture(iChannel2,(fragCoord+vec2(0.,-1.))*IRES).w);
        vec3 YP0 = normalize(vec3(((fragCoord+vec2(0.,1.)+SSOffset)*IRES*2.-1.)*(ASPECT*CFOV),1.))*YD.x;
        vec3 YP1 = normalize(vec3(((fragCoord-vec2(0.,1.)+SSOffset)*IRES*2.-1.)*(ASPECT*CFOV),1.))*YD.y;
        if (YD.x>-0.5) Bit = YP0-PixelVP;
        if (YD.y>-0.5 && (YD.x<-0.5 || length(Bit)>length(YP1-PixelVP))) Bit = YP1-PixelVP;
        vec3 Normal = normalize(cross(Bit,Tan))*EyeMat;
        Normal *= sign(dot(Normal,-Dir));
        Output.w = Vec3ToFloat(Normal*0.5+0.5);




        //
        //CURRENT FRAME: Temporal accumulation + TAA + Composition
        //
        float CurrentFrame = float(iFrame-1);
        SSOffset = SSOffsets[(iFrame-1)%16];
        CurrentTime = texture(iChannel1,vec2(0.5,0.5)*IRES).y;
        vec4 CurrentMouse = texture(iChannel1,vec2(2.5,0.5)*IRES);
        Pos = Position(CurrentMouse,CurrentTime,IRES);
        Eye = normalize(CameraCenter(CurrentMouse,CurrentTime)-Pos);
        Bit = TBN(Eye,Tan);
        EyeMat = TBN(Eye);
        Dir = normalize(vec3(((fragCoord+SSOffset)*IRES*2.-1.)*CFOV*ASPECT,1.)*EyeMat);
        float Distance = texture(iChannel1,fragCoord*IRES).w;
        if (Distance>-0.5) {
            //Geometry
            HIT Pixel = Trace(Pos+Dir*(Distance-0.01),Dir,CurrentTime); //Get attributes
            vec3 Normal = normalize(FloatToVec3(texture(iChannel3,fragCoord*IRES).w)*2.-1.);
            vec3 PPos = Pos+Dir*Distance;
            vec4 RefShad = texture(iChannel0,fragCoord*IRES);
            //Denoisers
            if (Pixel.M>2.5) {
                //Emissive
                vec3 WindowLight = SampleWindow(Pos,Dir,Pixel.M);
                Output.xyz = vec3(Vec2ToFloat(WindowLight.xy*IReflConst),Vec2ToFloat(vec2(WindowLight.z*IReflConst,0.)),0.);
            } else if (Pixel.M<1.) {
                //Non-emissive material
                vec3 BCRef = texture(iChannel2,fragCoord*IRES).xyz;
                vec3 ReflectionLight = vec3(FloatToVec2(BCRef.x)*ReflConst,BCRef.y);
                float Shadow = BCRef.z;
                vec3 FinalColor = vec3(0.);
                //Reprojection
                float PriorTime = texture(iChannel1,vec2(0.5,0.5)*IRES).z;
                vec4 PriorMouse = texture(iChannel1,vec2(3.5,0.5)*IRES);
                vec3 PriorPos = Position(PriorMouse,PriorTime,IRES);
                vec3 PriorEye = normalize(CameraCenter(PriorMouse,PriorTime)-PriorPos);
                vec3 PriorTan; vec3 PriorBit = TBN(PriorEye,PriorTan);
                vec3 PriorVPos = vec3(dot(PPos-PriorPos,PriorTan),dot(PPos-PriorPos,PriorBit),dot(PPos-PriorPos,PriorEye));
                vec2 PriorUV = ((PriorVPos.xy/PriorVPos.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
                if (DFBox(PriorUV-3.,RES-6.)<0.) {
                    //
                    //Reflections
                    //
                    vec3 LReflectionLight = FloatToVec3(texture(iChannel3,PriorUV*IRES).z)*ReflConst;
                    //Clamping
                    vec3 RMIN = vec3(1000.);
                    vec3 RMAX = vec3(0.);
                    for (float x=-1.; x<1.5; x+=1.) {
                        for (float y=-1.; y<1.5; y+=1.) {
                            BCRef.xy = texture(iChannel2,(fragCoord+vec2(x,y))*IRES).xy;
                            vec3 Sample = vec3(FloatToVec2(BCRef.x)*ReflConst,BCRef.y);
                            RMIN = min(RMIN,Sample);
                            RMAX = max(RMAX,Sample);
                        }
                    }
                    LReflectionLight = clamp(LReflectionLight,RMIN,RMAX);
                    //Output
                    ReflectionLight = (ReflectionLight+LReflectionLight*15.)/16.;




                    //
                    //Shadows
                    //
                    float LShadow = FloatToVec2(texture(iChannel3,PriorUV*IRES).y).y;
                    //Clamping
                    float SMIN = 1000.;
                    float SMAX = 0.;
                    for (float x=-1.; x<1.5; x+=1.) {
                        for (float y=-1.; y<1.5; y+=1.) {
                            BCRef.x = texture(iChannel2,(fragCoord+vec2(x,y))*IRES).z;
                            SMIN = min(SMIN,BCRef.x);
                            SMAX = max(SMAX,BCRef.x);
                        }
                    }
                    LShadow = clamp(LShadow,SMIN,SMAX);
                    //Output
                    Shadow = (Shadow+LShadow*15.)/16.;
                }




                //
                //Indirect diffuse
                //
                vec3 IndirectDiffuse = vec3(0.);
                if (!(PPos.y<0.001 && Normal.y>0.99)) {
                    IndirectDiffuse += vec3(0.2,0.1,0.04)*1.5*
                            (SolidAngle(PPos,Normal,vec3(4.,0.,2.),vec3(4.,0.,1.),vec3(2.,0.,2.))+
                            SolidAngle(PPos,Normal,vec3(1.8,0.,1.),vec3(0.,0.,1.2),vec3(0.,0.,4.))+
                            SolidAngle(PPos,Normal,vec3(2.,0.,4.),vec3(0.,0.,1.2),vec3(0.,0.,4.))
                            );
                }
                if (!(PPos.z>3.999 && Normal.z<-0.99)) {
                    IndirectDiffuse += vec3(1.,0.2,0.2)*0.7*
                            (SolidAngle(PPos,Normal,vec3(1.,0.,4.),vec3(0.,0.,4.),vec3(1.,1.5,4.)));
                }
                IndirectDiffuse /= (2.*PI);




                //
                //Composition
                //
                vec3 DiffuseLight = SunLight*Shadow*max(0.,dot(SunDir,Normal));
                DiffuseLight = max(vec3(0.),DiffuseLight)*Pixel.DC+IndirectDiffuse*Pixel.DC;
                vec3 FresnelTerm = SchlickFresnel(vec3(Pixel.Specular),dot(Normal,-Dir));
                FinalColor = DiffuseLight*(1.-Pixel.Metal)+Pixel.Metal*mix(DiffuseLight,ReflectionLight,FresnelTerm);




                //
                //Output
                //
                Output.xyz = vec3(Vec2ToFloat(FinalColor.xy*IReflConst),
                                  Vec2ToFloat(vec2(FinalColor.z*IReflConst,Shadow)),
                                  Vec3ToFloat(ReflectionLight*IReflConst));
            }
        } else {
            //Sky
            Output.xyz = vec3(0.);
            Output.xyz = vec3(Vec2ToFloat(Output.xy*IReflConst),
                                  Vec2ToFloat(vec2(Output.z*IReflConst,0.)),0.);
        }
    }
    fragColor = Output;
}

//TAA
    //Catmull-rom by hornet: https://www.shadertoy.com/view/MtVGWz

vec4 textureCube(vec2 UV) {
    //Samples the cubemap
    float Sign = -mod(floor(UV.y*I1024),2.)*2.+1.;
    vec3 D = vec3(vec2(UV.x,mod(UV.y,1024.))*I512-1.,Sign);
    if (UV.y>4096.) D = D.xzy;
    else if (UV.y>2048.) D = D.zxy;
    return texture(iChannel3,D);
}

vec3 sampleLevel0(vec2 PriorUV) {
    float YOffset = floor(PriorUV.x*I1024)*1024.+floor(PriorUV.y*I1024)*3072.;
    return textureCube(mod(PriorUV,1024.)+vec2(0.,YOffset)).xyz;
}

vec3 SampleTextureCatmullRom(vec2 uv) {
    vec2 samplePos = uv;
    vec2 texPos1 = floor(samplePos - 0.5) + 0.5;
    vec2 f = samplePos - texPos1;
    vec2 w0 = f * ( -0.5 + f * (1.0 - 0.5*f));
    vec2 w1 = 1.0 + f * f * (-2.5 + 1.5*f);
    vec2 w2 = f * ( 0.5 + f * (2.0 - 1.5*f) );
    vec2 w3 = f * f * (-0.5 + 0.5 * f);
    vec2 w12 = w1 + w2;
    vec2 offset12 = w2 / w12;
    vec2 texPos0 = texPos1 - vec2(1.0);
    vec2 texPos3 = texPos1 + vec2(2.0);
    vec2 texPos12 = texPos1 + offset12;
    vec3 result = vec3(0.);
    result += sampleLevel0( vec2(texPos0.x,  texPos0.y)) * w0.x * w0.y;
    result += sampleLevel0( vec2(texPos12.x, texPos0.y)) * w12.x * w0.y;
    result += sampleLevel0( vec2(texPos3.x,  texPos0.y)) * w3.x * w0.y;
    result += sampleLevel0( vec2(texPos0.x,  texPos12.y)) * w0.x * w12.y;
    result += sampleLevel0( vec2(texPos12.x, texPos12.y)) * w12.x * w12.y;
    result += sampleLevel0( vec2(texPos3.x,  texPos12.y)) * w3.x * w12.y;
    result += sampleLevel0( vec2(texPos0.x,  texPos3.y)) * w0.x * w3.y;
    result += sampleLevel0( vec2(texPos12.x, texPos3.y)) * w12.x * w3.y;
    result += sampleLevel0( vec2(texPos3.x,  texPos3.y)) * w3.x * w3.y;
    return max(vec3(0.),result);
}

void mainCubemap(out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir) {
    vec4 Output = vec4(0.,0.,0.,0.);
    vec2 UV; vec3 aDir = abs(rayDir);
    if (aDir.z>max(aDir.x,aDir.y)) {
        //Z-side
        UV = floor(((rayDir.xy/aDir.z)*0.5+0.5)*1024.)+0.5;
        if (rayDir.z<0.) UV.y += 1024.;
    } else if (aDir.x>aDir.y) {
        //X-side
        UV = floor(((rayDir.yz/aDir.x)*0.5+0.5)*1024.)+0.5;
        if (rayDir.x>0.) UV.y += 2048.;
        else UV.y += 3072.;
    } else {
        //Y-side
        UV = floor(((rayDir.xz/aDir.y)*0.5+0.5)*1024.)+0.5;
        if (rayDir.y>0.) UV.y += 4096.;
        else UV.y += 5120.;
    }
    vec2 RESOffset = vec2((mod(floor(UV.y*I1024)+0.5,3.)-0.5)*1024.,floor(UV.y*I1024*I3)*1024.);
    vec2 CUV = mod(UV,1024.)+RESOffset;
    if (DFBox(CUV-3.,RES-6.)<0.) {
        //Inside the screen
        vec2 BCRef = texture(iChannel0,CUV*IRES).xy;
        vec3 FinalColor = vec3(FloatToVec2(BCRef.x),FloatToVec2(BCRef.y).x)*ReflConst;
        //Reprojection
        float CurrentFrame = float(iFrame-1);
        vec2 SSOffset = SSOffsets[(iFrame-1)%16];
        float CurrentTime = texture(iChannel1,vec2(0.5,0.5)*IRES).y;
        vec4 CurrentMouse = texture(iChannel1,vec2(2.5,0.5)*IRES);
        vec3 Pos = Position(CurrentMouse,CurrentTime,IRES);
        vec3 Eye = normalize(CameraCenter(CurrentMouse,CurrentTime)-Pos);
        vec3 Tan; vec3 Bit = TBN(Eye,Tan);
        mat3 EyeMat = TBN(Eye);
        vec3 Dir = normalize(vec3(((CUV+SSOffset)*IRES*2.-1.)*CFOV*ASPECT,1.)*EyeMat);
        float Distance = texture(iChannel1,CUV*IRES).w;
        if (Distance<-0.5) Distance = 100000.; //Sky pixel
        HIT Pixel = Trace(Pos+Dir*(Distance-0.01),Dir,CurrentTime);
        vec3 Normal = normalize(FloatToVec3(texture(iChannel1,CUV*IRES).x)*2.-1.);
        vec3 PPos = Pos+Dir*Distance;
        float PriorTime = texture(iChannel1,vec2(0.5,0.5)*IRES).z;
        vec4 PriorMouse = texture(iChannel1,vec2(3.5,0.5)*IRES);
        vec3 PriorPos = Position(PriorMouse,PriorTime,IRES);
        vec3 PriorEye = normalize(CameraCenter(PriorMouse,PriorTime)-PriorPos);
        vec3 PriorTan; vec3 PriorBit = TBN(PriorEye,PriorTan);
        vec3 PriorVPos = vec3(dot(PPos-PriorPos,PriorTan),dot(PPos-PriorPos,PriorBit),dot(PPos-PriorPos,PriorEye));
        vec2 PriorUV = ((PriorVPos.xy/PriorVPos.z)*0.5/(ASPECT*CFOV)+0.5)*RES;
        if (DFBox(PriorUV-3.,RES-6.)<0.) {
            //Valid reprojection
            vec3 LFinalColor;
            if (length(PriorUV-CUV-SSOffset)>0.02) {
                //Catmull-rom sampling
                PriorUV -= SSOffsets[(iFrame-2)%16];
                LFinalColor = SampleTextureCatmullRom(PriorUV);
            } else {
                //Nearest neighbour sampling
                PriorUV = floor(PriorUV)+0.5;
                float YOffset = floor(PriorUV.x*I1024)*1024.+floor(PriorUV.y*I1024)*3072.;
                LFinalColor = textureCube(mod(PriorUV,1024.)+vec2(0.,YOffset)).xyz;
            }            
            //Clamping
            vec3 FMIN = vec3(1000.);
            vec3 FMAX = vec3(0.);
            for (float x=-1.; x<1.5; x+=1.) {
                for (float y=-1.; y<1.5; y+=1.) {
                    BCRef = texture(iChannel0,(CUV+vec2(x,y))*IRES).xy;
                    vec3 Sample = vec3(FloatToVec2(BCRef.x),FloatToVec2(BCRef.y).x)*ReflConst;
                    FMIN = min(FMIN,Sample);
                    FMAX = max(FMAX,Sample);
                }
            }
            LFinalColor = clamp(LFinalColor,FMIN,FMAX);
            //Output
            Output = vec4((FinalColor+LFinalColor*15.)/16.,0.);
        } else {
            //Invalid reprojection
            Output.xyz = FinalColor;
        }
    }
    //Output
    fragColor = Output;
}
