float diam = 0.1;
float dens = 4.;
float bgcoef = 1.2;

float sdCircle(vec2 uv, float r, vec2 offset) {
    float v = length(vec2(uv - offset)) / r;
    v = mod(v, 1.5);
    return clamp(dens-dens*abs(2.*v-2.), 0., 1.)*2. - bgcoef;
}

float sdNegSquare(vec2 uv, float r, vec2 offset) {
    float v = min(abs(uv.x - offset.x), abs(uv.y - offset.y)) / r;
    v = mod(v, 1.5);
    return clamp(dens-dens*abs(2.*v-2.), 0., 1.)*2. - bgcoef;
}

float smin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

float smax(float a, float b, float k) {
  return -smin(-a, -b, k);
}

float mixer(float a, float b, float k) {
    return smax(smin(a, b, k), -smax(a, b, -k), k);
}

vec3 drawScene(vec2 uv) {
    float d1 = sdCircle(uv, diam, vec2(sin(iTime) * .3, cos(iTime) * .5));
    float d2 = sdNegSquare(uv, diam, vec2(-sin(iTime) * .5, cos(iTime)) * .3);

    float c = mixer(d1, d2, 0.2);

    return vec3(c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = drawScene(uv);

    fragColor = vec4(col,1.0);
}
