float paintSDFWithLines(float sdf, float frequency, float size, float direction){
    return step(sdf, size+cos(direction*frequency+iTime));
}

float heart(vec2 uv, float inclination){
    return length(vec2(uv.x, uv.y-abs(uv.x*inclination)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv*=4.;

    float h = heart(uv, 0.6);

    vec3 col = vec3(paintSDFWithLines(h, 40., 0.4, uv.x)) * vec3(1., .5, 0.);

    fragColor = vec4(col,1.0);
}
