#define AA 8.

void _mainImage( out vec4 c, in vec2 p )
{
    vec2 z = (p*3.-iResolution.xy)/iResolution.y-vec2(1.3, .5), m = z;
    float i = 0.;
    for(;i < 50. && length(z = vec2(z.x*z.x - z.y*z.y + m.x, 2.*z.x*z.y + m.y)) < 14.; i++) {}
    c += cos(mix(
                 pow(texture(iChannel0, vec2(abs(z.y)-z.x*0.6, 0)*0.05).r, 8.)*1.5, // inside set
                 i/33., // outside
                 1.-float(i>47.))+vec4(5, 4, 2, 0))/AA;
}

void mainImage(out vec4 c, in vec2 p)
{
    c = vec4(0);
    for(float i = 0.; i < AA; i++)
        _mainImage(c, p + (fract(sin(p*25.6)*cross(p.xyx + iTime, p.yxx + i*216.165).xy)-0.5)); // terrible hash lol
}
