float circle(vec2 uv, vec2 p, float r, float s) {

    float d = length(uv - p);

    float c = smoothstep(r, r -s, d);
    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 sunPos = vec2(0., 0.);
    vec2 moonPos = vec2(-0.13, 0.);

    moonPos *= vec2((1. * iTime * .3) - 6. , 0.);

    float mask; 
    float bloom = circle(uv, sunPos, .47, .17); //sun with bloom

    mask += bloom;
    mask -= circle(uv, moonPos, .3, .007);

    vec3 col = vec3(1., 1., 0.);

    col *= mask;
    fragColor = vec4(col, 1.0);
}
