void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.,0.,0.);
    float FracTime = fract(iTime*0.3);

    vec3 color1 = vec3(34, 87, 122)/255.;
    vec3 color2 = vec3(56, 163, 165)/255.;
    vec3 color3 = vec3(87, 204, 153)/255.;
    vec3 color4 = vec3(128, 237, 153)/255.;

    col = (length(uv.x)>FracTime || length(uv.y)>FracTime) ? color1 : color2;
    FracTime = (FracTime+0.5)-floor(FracTime+0.5);
    col += (length(uv.x)>FracTime || length(uv.y)>FracTime) ? color3 : color4;


    fragColor = vec4(col, 1.0);
}
