float box(in vec2 _st, in vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size,
                        _size+vec2(0.001),
                        _st);
    uv *= smoothstep(_size,
                    _size+vec2(0.001),
                    vec2(1.0)-_st);
    return uv.x*uv.y;
}

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float plus(in vec2 _st, float _size){
    return  box(_st, vec2(_size,_size/4.)) +
            box(_st, vec2(_size/4.,_size));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    float h_scale = iResolution.x/iResolution.y;
    st.x *= h_scale;


    vec3 color = vec3(0.0);

    // To move the cross we move the space
    st -= vec2(0.825,0.5);

    vec2 translate = vec2(cos(-iTime * 3.),sin(4.* iTime));
    st = rotate2d( sin(iTime)*3.14 ) * st;
    st *= scale(vec2(2. * sin(iTime / 2.)));

    st += vec2(0.5);

    // Add the shape on the foreground
    color += vec3(plus(st + translate * 0.3 ,0.25));
    color -= vec3(plus(st + translate * 0.2 ,0.25));
    color += vec3(plus(st + translate * 0.1 ,0.25));
    color -= vec3(plus(st + translate * 0. ,0.25));

    color += vec3(plus(st + translate * -0.3 ,0.25));
    color -= vec3(plus(st + translate * -0.2 ,0.25));
    color += vec3(plus(st + translate * -0.1 ,0.25));
    color -= vec3(plus(st + translate * -0. ,0.25));

    fragColor = vec4(color,1.0);
}
