#define PI 3.141592
#define ORBS 20.

mat2 rotationMatrix(float angle)
{
	angle *= PI / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, s, -s, c );
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
  uv *= 200.27;


  fragColor = vec4(0.);
  for (float i = 0.; i < ORBS; i++) {
   vec2 trs = vec2(cos(iTime*20.1),sin(iTime*2.3));
 uv+=trs;
     uv *= rotationMatrix( 1.0 * -iTime );
    uv.xy += i * 2.05 * sin(uv.x / 10.32 + iTime) * 3.21 * cos(uv.y / 16.92 + iTime / 3.) * 0.21;
    float t = 1.1 * i * PI /   (2. + 1.) + iTime / 1.;
    float x = -2. ;
    float y = sin(t / 10.5795); 
    vec2 p = (1. * vec2(x, y)) / sin(PI * sin(uv.xy / 1.28 + iTime));
    vec3 col = cos(vec3(0, 1, -1) * PI * 2. / 3. + PI * (5. + i / 5.)) * 0.5 + 0.5;
    fragColor += vec4(i / 40. * 55.94 / length(uv - p * 1.9) * col, 3.57);
  }
  fragColor.xyz = pow(fragColor.xyz, vec3(3.57));
  fragColor.w = 1.0;
}
