#define PI 3.14
#define background vec3(0.0)
#define color vec3(0.0,1.0,1.0)
//#define low 2.0  //shows low frequencies more (also makes it asymmetric)

#ifndef low
#define low 1.0
#endif
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.0)/iResolution.y;
    vec2 polar = vec2(0.5+atan(uv.x,uv.y)/(2.0*PI),length(uv));
    float value = mix(
                    texture(iChannel0, vec2(1.0-polar.x,0.0)).r,
                    texture(iChannel0, vec2(polar.x,0.0)).r,
                    min(polar.x*low,1.0)
                  )*0.5;
    float size = texture(iChannel0, vec2(0,0.0)).r*0.3;
    vec3 col = background;
    if(value+size>polar.y) {
      col=color;
      if(size>polar.y) {
        col = texture(iChannel1, 0.5+uv/2.0/size).rgb;
      }
    }

    fragColor = vec4(col,1.0);
}
