void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy ) / iResolution.y;

    float a = cos(30.0 * uv.x * uv.y);
    a = clamp(a * 20.0, -1.0, 1.0);
    vec3 col = 0.5 + 0.5*vec3(a);

    float theta = iTime * 0.2;
    mat2 rotmat = mat2(cos(theta), sin(theta),
                       -sin(theta), cos(theta));
    uv *= rotmat;

    a = cos(30.0 * uv.x * uv.y);
    a = clamp(a * 20.0, -1.0, 1.0);
    col = col - (0.5 + 0.5*vec3(a));

    col.xy *= uv.xy;

    fragColor = vec4(col,1.0);
}
