vec3 pal( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.2, 0.7, 0.6);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.444, 0.2354, 0.111);

    return a + b*cos( 7.11221*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float len = -abs(length(uv));
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {
        uv = fract(uv * 2.0) - 0.5;

        float dist = length(uv) * len;

        vec3 color = pal(len++ + i*0.4 + iTime*0.4);

        dist = sin(dist*8.0 + iTime)/8.0;
        dist = abs(dist);

        dist = pow(0.01 / dist, 1.2);

        finalColor += dist / color;
    }

    fragColor = vec4(finalColor, 1.0);
}
