#define rot2D(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float gab(vec2 uv, float ti, float sc, float ph){

return sc*sin(ti*uv.x+ph)*(exp(-3.*length(uv)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float minres = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord - .5*iResolution.xy) / minres;
    vec2 uvs = uv;
    float ti = 1.1*sin(iTime/1.4+sin(iTime/2.+.4));


    uv *= (sin(iTime) + 2.)*12.;
    vec3 col;
    float i = 0.;
    while(i<192.){

    col.x += gab(uv+i/13.,4., i*2., iTime*6.);
    col.y += gab(uv+i/13.2,4., i*2.*length(uvs), iTime*6.1);
    col.z += gab(uv+i/13.5,4., i*4.*length(uvs), iTime*6.2);
    uv = rot2D(i/11.)*uv;

    i += (ti+12.1)/2111.+.55;


    }
    col *= 0.019;
    col *= pow(1.-length(uvs),2.6);
    col = (1.-col)-0.92;

    fragColor = vec4(col,1.0);
}
