vec2 rand( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 3.5;
    float falloff = 0.25;
    float sharpness = 1.;


    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;

    vec2 mousey = vec2(iMouse.x, iMouse.y)/iResolution.xy;
    mousey -= (uv+1.)*0.5;
    float mousee = clamp(smoothstep(pow(length(mousey),falloff),0.1,0.35)*-1.5+1.,0.,1.);

    uv.x *= iResolution.x / iResolution.y;

    vec2 uv1 = round(uv*scale+0.5)/scale;
    vec2 rand1 = rand(uv1)*2.;
    uv1 = (fract(uv*scale) * 2. - 1.) + rand1;
    uv1.x += sin(iTime - rand1.x*3.);
    uv1.y += cos(iTime - rand1.y*3.);
    uv1 *= mousee;

    uv -= uv1;

    vec3 uv2 = vec3(pow(abs(sin(uv.y*uv.x*5.+iTime*2.)), sharpness), pow(abs(sin(uv.y*uv.x*5.+iTime*0.5)), sharpness), pow(abs(sin(uv.y*uv.x*5.+iTime*-1.5)), sharpness));

    vec3 uv2r = uv2.x * vec3(0.961,0.302,0.020);
    vec3 uv2g = uv2.y * vec3(1.000,0.929,0.141);
    vec3 uv2b = uv2.z * vec3(0.141,0.514,1.000);

    fragColor = vec4(uv2r+uv2g+uv2b-vec3(0.3),1.0);
}
