mat2 Rot(float a){
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

float Twister(vec2 uv, vec2 s, vec2 r, float size, float v, float vr, float b){
    float t = iTime * v;
    uv*=Rot(iTime*vr);

    float scale = mix(s.x, s.y, sin(t)*.5+.5);
    uv *= Rot(length(uv*scale));


    float radio = mix(r.x, r.y, sin(t)*.5+.5);
    float c = smoothstep(b, .0, length(uv-radio)-size);
    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    float t = iTime;


    float c1 = Twister(uv, vec2(50., 80.), vec2(0., .2), .09, -1., 10., .1);
    float c2 = Twister(uv, vec2(10., 100.), vec2(0., .15), .13, .8, 2., .2);
    float c3 = Twister(uv, vec2(20., 90.), vec2(0., .17), .11, -1.3, 4., .02);
    float c4 = Twister(uv, vec2(15., 70.), vec2(0., .12), .12, 1.1, 20., .05);


    col = mix(col, vec3(.6), c1);
    col = mix(col, vec3(.4), c2);
    col = mix(col, vec3(.7), c3);
    col = mix(col, vec3(1.), c4);

    //Center Black
    col = mix(col, vec3(.7),smoothstep(.1, .0,length(uv)-.06));
    col *= vec3(0., .3, .5);

    fragColor = vec4(col,1.0);
}
