void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    // Normalized pixel coordinates from 0 to 1
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0.2,2,4));

    // Calculate the exploding star fractal
    float fractal = 0.0;
    vec2 pos = uv;
    for (int i = 0; i < 10; i++) {
        pos *= 2.0;
        fractal += 0.5 / (1.0 + dot(pos, pos));
    }

    // Output to screen
    fragColor = vec4(col * fractal, 1.0);
}
