// rotate
#define r(v) mat2(cos(v * iTime + vec4(0, 11, 33, 0)))

void mainImage(out vec4 o, vec2 u) {
    float d, s, i, j;

    // screen
    vec3 R = iResolution, q,  
         D = (vec3(u, R.y) - R/2.) / R.y;
         o *= i; 

    //p.z = -3.;

    while(i++ < 44.) {

        // cam
        q.z -= 4.,
        q.xz *= r(.8), 
        q.xy *= r(.7), 
        q.zy *= r(1.), 

        // sdf
        s = length(q.yz) + .1,
        s = max(
                -max(s - 1.2, abs(q.x + .9) - 1.1), 
                max(s - 1.4, abs(q.x) - .4)),
        q.x += .4,
        s = max(s, -min(length(q.xy), length(q.xz))  + .15),

        j = 64.;
        while(j < 512.) 
            q.x > .4 && q.x < .65 
                ? s += .1 / j 
                    * abs(sin(atan(q.y, q.z) * .3 * j) * 10.) 
                : s,
            j += j;

        // raymarch
        d += max(s = abs(s), 2e-3), 
        q = d * D,


        // material
        s < .02 
            ? o += (.01 - s) / d * i / 3.1 
            : o;
    }
}
