void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = 0.5*(0.9 + 0.1*texture(iChannel1,fragCoord/iResolution.xy))/(0.5+texelFetch(iChannel0, ivec2(fragCoord), 0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 fc = ivec2(fragCoord);
    if(max(fc.x,fc.y) > SPEED) discard;


    if(iFrame == 0) fragColor = vec4(0);
    else
    {
        float f = hash(vec3(fragCoord,iFrame));
        fragColor = texelFetch(iChannel0, fc, 0);
        int n = 1; // 85%
        if(f <= 0.01) n = 0; // 1%
        else if(f <= 0.08) n = 2; // 7%
        else if(f <= 0.15) n = 3; // 7%

        fragColor.xy = fragColor.xy*acbd[n] + ef[n];
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    uv *= 11.;
    uv.y += 5.;

    fragColor = texelFetch(iChannel1, ivec2(fragCoord), 0) * 0.997;
    if(iFrame == 0) fragColor = vec4(0);

    for(ivec2 p = ivec2(0,0); p.x <= SPEED; p.x++)
    {
        for(p; p.y <= SPEED; p.y++)
        {
            float d = distance(uv, texelFetch(iChannel0, p, 0).xy);
            fragColor += 0.00004/(0.00004+d*d);
        }
    }
}

#define SPEED 24

// Constants for the "Black Spleenwort" fern. (Matrix is in a different order than GLSL takes)
const mat2[4] acbd = mat2[](
mat2(0, 0, 0, 0.16),           /*f1*/
mat2(0.85, -0.04, 0.04, 0.85), /*f2*/
mat2(0.20, 0.23, -0.26, 0.22), /*f3*/
mat2(-0.15, -0.26, -0.28, 0.24)  /*f4*/
);

const vec2[4] ef   = vec2[](
vec2(0, 0),   /*f1*/
vec2(0, 1.6), /*f2*/
vec2(0, 1.6), /*f3*/
vec2(0, 0.44) /*f4*/
);
