// William Sidis' Checkerboard

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float Colsinx = sin(uv.x + iTime);
    float Colsiny = sin(uv.y + iTime);
    float sinparty = tan((Colsinx * Colsiny) * iTime);
    float sinpeace = tan((Colsinx / Colsiny) * iTime);

    bool casecraz = ((tan(uv.x * uv.y)) <= tan(uv.x / uv.y) || ((tan(uv.x) * tan(uv.y)) <= tan(uv.x / uv.y)));

    // change equality to change look
    if (casecraz == true) 
    {
        // Output to screen
        fragColor = vec4(Colsinx, Colsiny, sinparty, sinparty); // Fixed layout
    }
}
