// CC0: Simplistic skyline
// For my own secret purposes created this simple city skyline

#define TIME        iTime
#define RESOLUTION  iResolution

float mod1(inout float p, float size) {
  float halfsize = size*.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

float hash1(float co) {
  co += 123.4;
  return fract(sin(co*12.9898) * 13758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = vec3(1.0-q.y);
  float t = 1.0;
  for (float i = 0.; i < 4.; ++i) {
    vec2 cp = p;
    cp /= 10.0/(10.0+i);
    float cn = mod1(cp.x, 0.05);
    float ch = hash1(cn+123.4*float(i))*smoothstep(32.0, 4.0, abs(cn))*step(1.0, abs(cn));
    t = min(t,mix(i*0.125, 1.0, step(0.7*ch, p.y)));
  }
  col *= t;
  col = sqrt(col);

  fragColor = vec4(col, 1.0);
}
