void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    // Sample webcam input
    vec3 webcamColor = texture(iChannel0, uv).xyz;

    // Calculate ripple effect
    float time = iTime * 1.0;
    float frequency = 10.0;
    float amplitude = 0.02;

    vec2 distortedUV = uv + amplitude * sin(uv.y * frequency + time);

    vec3 distortedColor = texture(iChannel0, distortedUV).rgb;

    webcamColor = mix(webcamColor, distortedColor, 0.5);

    webcamColor = mix(webcamColor, vec3(1.0), 0.1);

    fragColor = vec4(webcamColor, 0.5);
}
