#define PIXW (1./iResolution.y)
void mainImage( out vec4 O, in vec2 C )
{
    vec2 uv=(C.xy-.5*iResolution.xy)*PIXW;

    uv*=5.;
    vec2 uvID=floor(uv);
    uv=fract(uv);
    uv-=.5;

    if(int(uvID.x+uvID.y)%2==0)
        {
            uv*=2.;
            uv=fract(uv);
            uv-=.5;
        }

    uv=vec2(length(uv),atan(-uv.y,-uv.x));//polar system
    uv.y=uv.y/6.28+.5;

    //anim
    uv.y=uv.y-iTime*.2;
    uv.y-=floor(uv.y);
    float ct=uv.y+iTime*.8;ct-=floor(ct);

    O=vec4(smoothstep(0.+10.*PIXW,0.,uv.x-0.2*(sin(uv.y*10.*3.14)*.5+.8))
    );


    O=mix(vec4(.7,.3,.4,1.)*O,O*vec4(.1,.3,.2,1.),ct);

}
