float r(vec2 p){
    float m = fract(sin(dot(p, vec2(30.0,700.0))) * 4.0);
    return m;
}

float n(vec2 st) {
    vec2 n = floor(st);
    vec2 f = fract(st);

    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    return mix(mix(r(n + vec2(0.0,0.0)),
                    r(n + vec2(1.0,0.0)), f.x),
               mix(r(n + vec2(0.0,1.0)),
                    r(n + vec2(1.0,1.0)), f.x), f.y);
}

mat2 rot(float a){
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage(out vec4 f, in vec2 c) {
    vec2 u = c/iResolution.y;
    u.y *= 5.0;

    u = rot(n(u.xy + 3.0 - (u.x + u.y) * 3.0 + iTime * 0.3) * 1.9) * u;

    vec3 col = vec3(0.5 + 0.9 * sin(iTime + u.y + cos(iTime * 0.9 + u.x)), 
                    0.0 + 0.9 * sin(iTime + u.y + cos(iTime * 0.9 + u.x)),
                    0.0);

    f = vec4(col, 1.0);
}
