void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // parameters

    vec2 p[4];
    p[0] = vec2(0.1, 0.9);
    p[1] = vec2(0.9, 0.9);
    p[2] = vec2(0.5, 0.1);
    p[3] = vec2(cos(iTime), sin(iTime)) * 0.4 + vec2(0.5, 0.5);

    vec3 c[4];
    c[0] = vec3(0.996078431372549, 0.3411764705882353, 0.33725490196078434);
    c[1] = vec3(0.996078431372549, 0.6352941176470588, 0.1607843137254902);
    c[2] = vec3(0.1450980392156863, 0.8196078431372549, 0.8588235294117647);
    c[3] = vec3(1.0, 1.0, 0.0);

    float blend = 2.0;

    // calc IDW (Inverse Distance Weight) interpolation

    float w[4];
    vec3 sum = vec3(0.0);
    float valence = 0.0;
    for (int i = 0; i < 4; i++) {
        float distance = length(uv - p[i]);
        if (distance == 0.0) { distance = 1.0; }
        float w =  1.0 / pow(distance, blend);
        sum += w * c[i];
        valence += w;
    }
    sum /= valence;

    // apply gamma 2.2 (Approx. of linear => sRGB conversion. To make perceptually linear gradient)

    sum = pow(sum, vec3(1.0/2.2));

    // output

	fragColor = vec4(sum.xyz, 1.0);
}
