/* v1.5 */
float WaveMask(vec2 uv, float h, float t, float i) {
    vec2 st = uv;
    st.x += t;
    st.y += (sin(st.x*10.)*.5+.5)*i;

    float softness = 0.001;
    float c = smoothstep(h+softness, h ,st.y);
    return c;
}

float WaterMask(vec2 uv, float h, float t, float i) {
   float waterMask1 = WaveMask(uv, h, t*.4, i*0.05);
    float waterMask2 = WaveMask(uv, h, -t*.3, i*0.05);
    float waterMask3 = WaveMask(uv, h, t*.5, i*0.05);

    float waterMask = mix(waterMask1, waterMask2, .5);
    waterMask = mix(waterMask, waterMask3, .5);
    return waterMask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float h = 0.5;
    if (iMouse.xy != vec2(0.)) {
        h = iMouse.y/iResolution.y;
    }
    h *= 1.2;

    vec3 waterColor = vec3(28.,163.,236.)/255.;
    float o1 = sin(iTime*2.)*0.02;
    float o2 = sin(iTime*.5)*0.02;
    float o3 = sin(iTime)*0.02;

    float w1 = WaterMask(uv*vec2(1.,1.), o1+h-0.15, iTime, 1.);
    float w2 = WaterMask(uv*vec2(2.,1.), o2+h-0.06, iTime*.5, 0.8);
    float w3 = WaterMask(uv*vec2(3.,1.), o3+h, iTime*.3, 0.5);

    float w = mix(w1, w2, 0.5);
    w = mix(w, w3, 0.5);


    fragColor = vec4(w * waterColor, w);
}
