/* https://www.shadertoy.com/view/DdsGDj */
#define MOUSE_OFFSET ( iMouse.z > 0. ? iMouse.xy - iResolution.xy * 0.5 : vec2(0) )
vec2 map_to_centered_ndc(in vec2 SC, in float scale, in vec2 origin, in bool mouse_drag)
{
    vec2 M = MOUSE_OFFSET * (mouse_drag == true ? 1. : 0.);
    return ((2. * (SC - M) - iResolution.xy) / iResolution.y) * scale + origin;
}

vec3 get_background() { return vec3(.1, .4, .6); }

HIT find_closest_intersection(RAY ray)
{
    HIT c_hit; c_hit.hit_dist = 9e5;
    int hit_something = 0; // Set only once, when firts hit occurs.

    for (int i = 0; i < 3; ++i)
    {
        HIT hit;

        switch (i)
        {
        case 0: hit = ray_sphere_int(ray, vec3(0), 1.); break;
        case 1: hit = ray_sphere_int(ray, vec3(0,0,1), 0.5); break;
        case 2: hit = ray_sphere_int(ray, vec3(0,0,2), 0.25); break;
        }

        if (hit.hit_something == 1 &&       // Find intersection?
            c_hit.hit_dist > hit.hit_dist)  // Close then before?
        {
            c_hit = hit;
            c_hit.hitted_idx = i;
        }

        // If we find any hit, then this value will be set to 1 before the function exits.
        hit_something = max(hit.hit_something, hit_something);
    }

    // Save global hit flag.
    c_hit.hit_something = hit_something;
    return c_hit;
}

// You can also approximate normal like in 
// raymarching, but this routine less stable.
// Only for static scenes.
vec3 approximate_normal(RAY ray, HIT hit)
{
    const vec2 e = vec2(0.001, 0);
    RAY ray_;
    ray_.position = hit.hit_point - ray.direction*0.3;

    ray_.direction = ray.direction + e.xyy;
    HIT hit_0 = find_closest_intersection(ray_);

    ray_.direction = ray.direction + e.yxy;
    HIT hit_1 = find_closest_intersection(ray_);

    vec3 r = normalize(hit_0.hit_point - hit.hit_point),
         u = normalize(hit_1.hit_point - hit.hit_point),
         f = normalize(cross(r, u));
    //return f;   
    return f * ( dot(ray.direction, f) >= 0. ? -1. : 1. ); // small improvment
}

vec3 lambert(RAY ray, HIT hit) // lambert reflectance model
{
    vec3 light_pos = vec3(sin(iTime)*10.,10,10),
         hp_to_l = normalize(light_pos - hit.hit_point),
         obj_col = 1.-get_background(); 

    float diffuse = iMouse.z > 0. ?
        max(dot(hp_to_l, approximate_normal(ray, hit)), 0.) :
        max(dot(hp_to_l, hit.hp_normal), 0.);

    return hit.hit_something == 1 ?
        obj_col * diffuse :
        get_background();
}

vec3 scene(vec2 SC)
{
    // Generate primary ray.
    vec2 MP = iMouse.xy == vec2(0) ?
        vec2(0) :
        map_to_centered_ndc(iMouse.xy, 1., vec2(0), false);
    RAY ray = perspective_camera(SC, vec3(0,0,6), vec3(MP, 0), iResolution.xy);

    // Trace scene.
    HIT hit = find_closest_intersection(ray);

    // Process lights.
    return lambert(ray, hit);
}

// Basic anti-aliasing (supersample).
vec3 OSSAA(in vec2 SC)
{
    vec3 col = vec3(0);
    float order = 1.,          // [0; inf)
    inv = 1./(2.*order + 1.),
    blur = 1.;                 // (0, inf)

    for (float y = -order; y <= order; y += 1.0)
        for (float x = -order; x <= order; x += 1.0)
        {
            vec2 offset = (blur*vec2(x, y)) * inv;
            col += scene(SC + offset);
        }

    order = 2.*order + 1.;
    return col / (order*order);  
}

void mainImage( out vec4 O, in vec2 SC )
{
    //O = vec4(scene(SC),1.0);
    O = vec4(OSSAA(SC), 1.0);
}

// Structures.
struct RAY
{
    vec3 position;
    vec3 direction;
};
struct HIT
{
    int hit_something; // Global hit
    float hit_dist;
    vec3 hit_point;
    vec3 hp_normal;
    int hitted_idx;
};

// Cameras.
RAY perspective_camera(vec2 SC, vec3 position, vec3 look_at, vec2 resolution)
{
    float zFocalLength = 50.0; // mm.
    vec3 camera = position;

    vec3 f = normalize(look_at - camera);               // forward
    vec3 r = normalize(cross(vec3(0.0, -1.0, 0.0), f)); // right
    vec3 u = normalize(cross(r, f));                    // up

    float size = 36.0;        // Sensor Fit: Mode = Auto.    
    float aspectRatio = resolution.x / resolution.y;
    float vpWidth = size;
    float vpHeight = vpWidth / aspectRatio;

    // Before uv=[0;1][0;1]
    vec2 uv = SC / resolution.xy;
    uv.x = (uv.x * vpWidth) - vpWidth * 0.5;
    uv.y = (uv.y * vpHeight) - vpHeight * 0.5;
    // After uv=[-vpWidth*0.5; vpWidth*0.5][-vpHeight*0.5; vpHeight*0.5]

    return RAY(
        camera,
        normalize(uv.x * r + uv.y * u + f * zFocalLength));
}

// Ray-Object intersection routines.
HIT ray_sphere_int(
    RAY ray,
    vec3 sphere_pos,
    float sphere_r)
{
    vec3 rel_p = ray.position - sphere_pos;

    float half_b = dot(rel_p, ray.direction),
          c = dot(rel_p, rel_p) - sphere_r * sphere_r,
          discriminant = (half_b * half_b - c),
          sqrtDiscriminant = sqrt(discriminant),
          root = min(-half_b - sqrtDiscriminant, -half_b + sqrtDiscriminant);

    HIT hit;

    if (discriminant < 0. || // no real roots?
        root < 0.)           // behind camera?
    { 
        hit.hit_something = 0;
        hit.hit_dist = 9e5;
        return hit; 
    }

    hit.hit_something = 1;
    hit.hit_dist = root;
    hit.hit_point = ray.position + ray.direction * root;
    hit.hp_normal = normalize(hit.hit_point - sphere_pos);

    return hit;
}
