void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2. - 1.;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.; i < 3.; i++) {
        uv = fract(uv * 2.) - 0.5;

        float d = length(uv) * exp(-length(uv));

        vec3 col = vec3(1.0, 2., 3.);

        d = sin(d* 8. + iTime) / 8.;
        d = abs(d);
        d = 0.008/d;


        col *= d;

        finalColor += col * d;
    }  

    fragColor = vec4(finalColor, 1.);
}
