bool InMandel(vec2 z){
vec2 c =z;
    for(int i = 0;i<50;i++){
        z= vec2(z.x * z.x - z.y * z.y,2. * z.x * z.y) + c;
    }
    if(length(z) < 2.)return true;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 *iResolution.xy)/iResolution.y;
    uv *=2.5;
    uv.x;
    vec3 UVVec= vec3(uv,0);
    vec3 col = vec3(0);
    bool In= false;

    vec3 UVNorm = normalize(vec3(uv,1));
    vec3 lightPos= vec3(uv.xy,0) + vec3(0,0,10);

    vec3 l = normalize(lightPos - UVVec);

    col += dot(l,UVNorm)/1.2;
    if(InMandel(uv))col -=.5;

    fragColor = vec4(col,1.0);
}
