/* https://www.shadertoy.com/view/DdsGDj */
#define MOUSE_OFFSET ( iMouse.z > 0. ? iMouse.xy - iResolution.xy * 0.5 : vec2(0) )
vec2 map_to_centered_ndc(in vec2 SC, in float scale, in vec2 origin, in bool mouse_drag)
{
    vec2 M = MOUSE_OFFSET * (mouse_drag == true ? 1. : 0.);
    return ((2. * (SC - M) - iResolution.xy) / iResolution.y) * scale + origin;
}

// SDFs
float sphereSDF_L2(in vec3 P, in vec3 C, in float r) { return length(C - P)-r; }

/* SCENE */
#define NUM_OF_OBJECTS ( 3 )
vec3 obj_colors[NUM_OF_OBJECTS] = vec3[NUM_OF_OBJECTS](
    vec3(1,0,0), vec3(0,1,0), vec3(0,0,1)
);

float find_closest_scene_object(in vec3 marching_point, out int hitted_idx)
{
    float min_d = 9e5;
    for (int idx = 0; idx < NUM_OF_OBJECTS; ++idx)
    {
        float d;
        switch (idx)
        {
        case 0: d = sphereSDF_L2(marching_point, vec3(0., 0., 0.0), 0.9); break;
        case 1: d = sphereSDF_L2(marching_point, vec3(1., 0., 1.0), 0.4); break;
        case 2: d = sphereSDF_L2(marching_point, vec3(-1., 0., -1.0), 0.6); break;
        }

        min_d = min(min_d, d); // this also works as z-buffer.

        // Need to update index?
        hitted_idx = 
            min_d == d ?
                idx :
                hitted_idx;
    }
    return min_d;
}
/* SCENE */

HIT ray_march(
    RAY ray,
    float max_num_of_steps,
    float max_distance,
    float surface_distance) 
{
    float marched_distance = 0.0; // Distance marched from ray origin.
    float minimal_distance = 0.0; // Current distance to closest object.

    bool quit = false, hit = false;
    int hitted_idx = -1;

    for (float step = 0.; (step < max_num_of_steps) && (quit == false) && (hit == false); step += 1.)
    {
        // Calculate current coordinates at ray.
        vec3 marching_point = ray.origin + ray.direction * marched_distance;

        // Find distance to closest object to point.
        minimal_distance = find_closest_scene_object(marching_point, hitted_idx);

        // March.
        marched_distance += minimal_distance;

        hit = minimal_distance < surface_distance; // Hit some object?
        quit = marched_distance > max_distance;    // Marched into infinity?
    }

    return HIT(marched_distance, hit, hitted_idx);  
}

/* You can see how this works in 2d here:
   https://www.shadertoy.com/view/slyBRc */
vec3 approximate_normal(in vec3 P)
{
    int dummy = -1;
    const float delta = .01;          
    // Sample distance function at hit point and three nearby points.
    float s0 = find_closest_scene_object(P, dummy),
          s1 = find_closest_scene_object(P + vec3(delta, 0, 0), dummy),
          s2 = find_closest_scene_object(P + vec3(0, delta, 0), dummy),
          s3 = find_closest_scene_object(P + vec3(0, 0, delta), dummy);
    // Calculate finite differencies.    
    float dfdx = s1 - s0,
          dfdy = s2 - s0,
          dfdz = s3 - s0;
    // Approximate gradient/normal direction.
    return normalize(vec3(dfdx, dfdy, dfdz));
}

vec3 light(RAY ray, HIT hit)
{
    vec3 color = obj_colors[hit.hitted_idx],
         light_pos = vec3(0,10,10),
         hp = ray.origin + ray.direction * hit.hit_dist,
         normal = approximate_normal(hp),
         light_dir = normalize(light_pos - hp);
    return 
        hit.hit_something == true ?
            color * max(dot(light_dir, normal), 0.) : // only diffuse light
            1.-(normalize(hp) + vec3(1)) * 0.5;
}

RAY perspective_camera(vec2 SC, vec3 pos, vec3 look_at)
{
    float zFocalLength = 100.0; // mm.
    vec3 camera = pos;

    vec3 f = normalize(look_at - camera);                // forward
    vec3 r = normalize(cross(vec3(0.0, -1.0, 0.0), f)); // right
    vec3 u = normalize(cross(r, f));                    // up

    float size = 36.0;        // Sensor Fit: Mode = Auto.    
    float aspectRatio = iResolution.x / iResolution.y;
    float vpWidth = size;
    float vpHeight = vpWidth / aspectRatio;

    // Before uv=[0;1][0;1]
    vec2 uv = SC / iResolution.xy;
    uv.x = (uv.x * vpWidth) - vpWidth * 0.5;
    uv.y = (uv.y * vpHeight) - vpHeight * 0.5;
    // After uv=[-vpWidth*0.5; vpWidth*0.5][-vpHeight*0.5; vpHeight*0.5]

    RAY ray;
    return RAY(
        camera,
        normalize(uv.x * r + uv.y * u + f * zFocalLength));
}

vec3 scene(in vec2 SC)
{
    vec2 NDC = map_to_centered_ndc(SC, 1., vec2(0), false),
         MP =
             iMouse.xy == vec2(0) ?
                 vec2(0) :
                 map_to_centered_ndc(iMouse.xy, 1., vec2(0), false);

    RAY ray = perspective_camera(SC, vec3(0,0,10), vec3(MP, 0));

    // March through scene.
    HIT hit = ray_march(ray, MAX_NUM_OF_STEPS, MAX_MISS_DIST, MIN_HIT_DIST);
    vec3 color = light(ray, hit);
    return mix(color, 0.5-color, smoothstep(1./iResolution.y, 0., diskSDF(NDC, vec2(0), 0.01)));
}

vec3 RSSAA(in vec2 SC)
{
    vec3 col = vec3(0);
    float order = 6., blur = 1.0;

    for (float s = 0.; s < order; s += 1.0)
    {
        vec2 jitter = HASH(s, s*order) * 0.5;
        col += scene(SC + jitter * blur);
    }

    return col / order;  
}

void mainImage( out vec4 O, in vec2 SC )
{   
    //O = vec4(scene(SC), 1.); 
    O = vec4(RSSAA(SC), 1.);   
}

#define MAX_MISS_DIST    ( 1e4 )
#define MIN_HIT_DIST     ( 1e-4 )
#define MAX_NUM_OF_STEPS ( 300. )

// Data structures.
struct RAY
{
    vec3 origin;
    vec3 direction;
};
struct HIT
{
    float hit_dist;
    bool hit_something;
    int hitted_idx; // By default if no hit, then hitted_idx == -1.
};

/* 
    Other stuff 
*/

/* 
    Returns random vec2([-1.0; 1.0], [-1.0; 1.0]).
*/
vec2 hash2to2(
    const uint x,
    const uint y)
{
    // Pick some seeds.
    const uint s0 = 123u;
    const uint s1 = 456u;
    const uint s2 = 789u;

    // Mix coordinates.
    uint v0 = y * s2 + x;
    uint v1 = x * s2 + y;

    // Calculate hash.
	v0 += s1; v0 *= 445593459u; v0 ^= s0;
    v1 += s1; v1 *= 445593459u; v1 ^= s0;

    // 2.0f / 4294967295.0f = 4.6566128730773926e-10

    return 
        vec2(
            float(v0 * v0 * v0) * 4.6566128730773926e-10f - 1.0f,
            float(v1 * v1 * v1) * 4.6566128730773926e-10f - 1.0f);
}
#define HASH(x, y)                ( hash2to2(uint(x), uint(y)) )

float diskSDF(in vec2 NDC, in vec2 C, in float r) { return length(C - NDC)-r; }
float vertical_lineSDF(vec2 NDC, float x0) { return abs(NDC.x-x0); }
float segmentSDF(in vec2 NDC, in vec2 B, in vec2 E, in float r)
{
    vec2 BP = NDC - B, BE = E - B;
    return length(BP - BE * clamp(dot(BP, BE) / dot(BE, BE), 0.0, 1.0)) - r;
}
