void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized configurable factors
    float maxR = iMouse.x / iResolution.x;
    float smoothness = iMouse.y / iResolution.y;
    float minR = maxR * (1.0 - smoothness);
    vec2 centerUv = vec2(0.5, 0.5);

    float displayRatio = iResolution.x / iResolution.y;

    // just to play animation
    if (length(iMouse.xy) <= 10.0) {
        // radius to cover the fullscreen 
        float diagonalRadius = length(vec2(
            max(centerUv.x, (1.0 - centerUv.x)) * displayRatio, 
            max(centerUv.y, (1.0 - centerUv.y))
        ));
        maxR = diagonalRadius * (sin(iTime) + 1.0) * 0.5;
        minR = maxR * abs(sin(iTime));
    } 

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 duv = uv;
    duv.x *= displayRatio;
    centerUv.x *= displayRatio;

    vec3 col = texture(iChannel0, uv).rgb;
    float dist = length(centerUv - duv);
    if (dist > maxR) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        return;
    } 

    if (maxR > dist && dist > minR) {
        col = mix(col, vec3(0.0, 0.0, 0.0), smoothstep(minR/maxR,1.0,dist/maxR));
    }

    fragColor = vec4(col,1.0);
}
