void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Set up the variables
    vec2 resolution = vec2(420.0, 236.0);
    float time = iTime;
    vec2 center = vec2(0.5, 0.5);
    float radius = 0.05;
    vec2 position = center + vec2(0.1 * cos(time), 0.1 * sin(time));

    // Create a black box
    vec4 color = vec4(0.0, 0.0, 0.0, 1.0);

    // Draw a bright circle
    if (distance(position, fragCoord.xy / resolution) < radius) {
        color = vec4(1.0, 1.0, 1.0, 1.0);
    }

    // Output the fragment color
    fragColor = color;
}
