vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.2, 0.5);   // Mauve
    vec3 b = vec3(1.0, 1.0, 0.0);   // Jaune
    vec3 c = vec3(0.0, 1.0, 0.0);   // Vert
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++) {
        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        float t = length(uv0) + i * 0.2 + sin(iTime * 0.5) * 0.2;

        vec3 col = palette(t);

        // Ajout des formes triangulaires qui bougent
        float triangleSize = 0.1;  // Taille des triangles
        vec2 trianglePos = vec2(0.3, 0.5);  // Position initiale des triangles
        vec2 translatedUV = uv - trianglePos;
        float triangleMask = smoothstep(triangleSize, triangleSize + 0.01, abs(translatedUV.x) + abs(translatedUV.y));

        d = sin(d * 8.0 + iTime) / 8.0;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += mix(col, vec3(1.0), triangleMask) * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
