//based on original by kishimisu
void mainImage(out vec4 O, vec2 F) {
    vec2  R = iResolution.xy,
          u = (F+F-R) / R.y;
    float i,  l = length(u);

    for (O *= i; i++ < 4.;

         O += pow(0.027/ 
                 abs(
                     sin(
                         8. * ( length( u = fract(u * 1.3*(iMouse.x/256.0)) - .5 ) / exp(l) ) 
                         + iTime
                        ) // sin
                    )  // abs
              , 1.02) // pow

              * (1. + cos(6. * ( l + (i + (iMouse.y/1024.0))*.4 + vec4(.26,.4,.56,0) )))
    );
}
