float myMod(float v1, float v2) {
    return ((v1 / v2) - floor(v1 / v2));
}

vec3 palette(in float t)
{
    vec3 a = vec3(0.500, 0.500, 0.500);
    vec3 b = vec3(0.500, 0.500, 0.500);
    vec3 c = vec3(1.000, 1.000, 1.000);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

float lerp(float a, float b, float t){
    return a + (b-a) * t;
}

float getValue1( vec2 uv, float st ) {
    vec2 temp1= vec2(cos(iTime), sin(iTime)) * 0.2;
    vec2 temp2 = vec2(sin(iTime), cos(iTime)) * 0.1;

    vec3[5] balls = vec3[5](
        vec3(temp1.x      , temp1.y      , 0.2),
        vec3(temp2.x      , temp2.y + 0.5, 0.1),
        vec3(temp2.x - 0.5, temp2.y      , 0.1),
        vec3(temp2.x      , temp2.y - 0.5, 0.1),
        vec3(temp2.x + 0.5, temp2.y      , 0.1)
    );

    float dist = 999999.9;
    int bl = balls.length();
    for (int i = 0; i < bl; i++) {
        float currentDist = length(uv - balls[i].xy) - balls[i].z;
        dist = min(currentDist, dist);
    }

    float thresh = 0.05;
    return 1.0 - (smoothstep(0.0, thresh, abs(dist)));
}

float getValue2( vec2 uv, float st ) {
    vec2 center = vec2(cos(iTime), sin(iTime)) * -0.5;
    vec2 offset = uv - center;
    float a = max(abs(offset.x), abs(offset.y));
    float b = smoothstep(0.0, 1.0, length(offset));
    float v1 = lerp(a, b, st / 2.0);
    float thresh = 0.25;
    return 1.0 - smoothstep(thresh, thresh + 0.1, v1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float st = 1.0 + sin(iTime);
    float st2 = (1.0 + sin(iTime / 2.0)) / 2.0;
    float val1 = getValue1(uv, st);
    float val2 = getValue2(uv, st);
    float lum = lerp(val1, val2, st2);
    vec3 col = palette(lum - myMod(-iTime, 2.0));
    //vec3 col = vec3(1.,1.,1.);
    col *= lum;
    fragColor = vec4(col,1.0);
}
