mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1,1,-1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    //p=vec3(p.x*p.x+p.y*p.y, 4.*p.x*p.y, p.z);

    for(j=0.;j++<4.;)
        p=abs(abs(p/cos(sin(iTime*.5))*rotate3D(iTime, q))-.05)-.02,
        p-=refract((p),p.yzx,.6),
        d-=pow(dot(p,p),3.);

    e+=1.-d;
}o+=vec4(p/4.,1)*3./i;
}
