vec3 palette(float t) 
{
    vec3 a = vec3(0.6, 0.6, 0.6);
    vec3 b = vec3(0.480, 0.480, 0.480);
    vec3 c = vec3(.6, .6, .6);
    vec3 d = vec3(-1.112, -0.778, -0.445);

    return a + b * cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Bring into clip space and prevent stretching
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++) {
        uv = fract(uv * 1.2) - 0.5;

        float d = length(uv) * 2. * -pow(length(uv0), 1.3) * exp(length(uv0));

        vec3 col = palette(length(uv0) - iTime*.8);

        d = sin(d * 6. + iTime)/8.;
        d = abs(d);
        d = pow(0.02 / d, 1.5);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
