float fblur (float d, float radius, float blur){
    return smoothstep(radius-blur, radius+blur, d);
}

float lips(vec2 size, float radius, vec2 uv, vec2 pos){
    vec2 hSize = size / 2.0 - radius;
    return length(max(abs(uv - pos), hSize) - hSize);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord/iResolution.yy) * 2.0 - vec2(iResolution.x / iResolution.y, .7);    

    vec2 pos = vec2(0.,.1*sin(iTime));    
    float col = lips(vec2(2.+abs(.2*sin(iTime)), .8) + pos, .9, uv, pos);
    col = fblur(-col, -.8, .4);

    fragColor = vec4(col, 0., 0., col);
}
