#define PI 3.14159265359
float WaterLilyLeafSdf(vec2 p,float size,float cutsize)
{
   float invdoublecutsize=1./(2.*cutsize);
   float c=abs(fract(p.x*cutsize+0.5)-0.5)*size*2.;
   bool cutregion=p.x>invdoublecutsize||p.x<-invdoublecutsize;
   bool upcut=p.y>.0;
   float deformation=cutregion?size:(upcut?c:size);
   return length(p)-deformation;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x);


    float sdf=WaterLilyLeafSdf(uv,.5,4.);

    #if 1

    sdf=smoothstep(.033,.0,sdf);

    #else

    sdf=step(sdf,0.001);

    #endif

    vec3 color=vec3(0,1,0);

    fragColor = vec4( sdf*color, 1.0 );
}
