vec3 palette(float t) {
    vec3 a = vec3(0.600,0.827,1.000);
    vec3 b = vec3(0.502,1.000,1.000);
    vec3 c = vec3(0.200,0.800,1.000);
    vec3 d = vec3(-0.422, 0.638, -0.252);
    return a + b * cos(6.28318*(c*t+d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.* fragCoord - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.);
    float d;
    float t=iTime;
    float c=iTime*6.0;

    vec2 pos=vec2(0.5*sin(t),0.5*cos(t));
    d   = length(uv+pos)-0.15;

    d   = length(uv+pos);
    d   = 5. / d;
    col = palette(iTime + d/5.);

    col *= abs(sin(10.*d + c));

    fragColor = vec4(col,1.0);
}
