#define SCALE_X 1280.0
#define SCALE_Y 720.0
#define SQUEEZE_RATE 8.0
#define SPEED 100.0

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pixel = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    pixel *= vec2(SCALE_X, SCALE_Y);

    int squeeze = int(sin(iTime/4.0)*4.0);

    pixel.x = abs(pixel.x) * float(squeeze);
    pixel.y = abs(pixel.y) * float(squeeze);


    int x1 = int(pixel.x);
    float x = float(x1);

    int y1 = int(pixel.y);
    float y = float(y1);

    int t1 = int(iTime * SPEED);
    float t = float(t1);


    fragColor = vec4(
        mod(x + mod(y + t, 64.0) + y + mod(x + t, 64.0), 256.0) / 256.0,
        0,
        mod(x + mod(y + t, 128.0) + y + mod(x + t, 128.0), 256.0) / 256.0,
        1.0
    );
}
