#define circle_radius 100.0f
#define threshold 10000000000.5f

bool isNear(float x, float near, float t) {
    return x < near * (1.0f + t) && x > near * (1.0f - t);
}

bool isNear(float x, float near) {
    return isNear(x, near, threshold);
}

bool function(vec2 coordinate) { // EDIT ME!
    float x = coordinate.x;
    float y = coordinate.y;

    float leftSide = y*y;
    float rightSide = x*x*sin(x*x+y*y);

    return isNear(leftSide, rightSide);
}

void mainImage(out vec4 color, in vec2 coordinate) {
    vec2 center = iResolution.xy / 2.0f;
    vec2 mouse = iMouse.xy;
    if (mouse == vec2(0.0f, 0.0f)) mouse = center;
    bool func = function(coordinate - mouse);
    color = vec4(func, func, func, 1.0f);
}
