void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pos = fragCoord / iResolution.xy;
    pos = pos - vec2(0.5, 0.5);
    pos.x *= iResolution.x / iResolution.y;
    vec2 pos0 = pos;

    vec3 col = vec3(0.0);

    float scale = .55;
    float offset = 0.150;
    pos = (pos - vec2(0.5)) * scale + vec2(0.5) + vec2(offset);

    for (float i = 0.0; i < 3.0; i++)
    {
        pos = fract(pos + cos(pos * 0.4) / 0.7) - 0.5;
        vec2 origin = vec2(0.0);
        float p = distance(pos, origin);
        float theta = atan(pos.y, pos.x);
        p *= 4.0;
        p -= 0.5 * sin(5.0 * theta + iTime * 2.0 + (i + 2.0 + 20.33) / 25.);
        col[int(i)] = smoothstep(1.7, 0.4, p);
    }

    fragColor = vec4(col, 1.0);
}
