void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float d = length(uv);
    float zoom = 100.0;

    d = sin(d * zoom - (iTime * 100.));
    d = smoothstep(1., -1., d / fwidth(d));

    fragColor = vec4(d) - iTime * 0.01;
}
