// Tutorial followed: https://www.youtube.com/watch?v=8bbTkNZYdQ8

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ///

    float angle = iTime * .1;

    // Normalize uv
    vec2 uv = fragCoord / iResolution.xy * 2. - 1.;
    for(float i = 0.; i < 32.; i += 1.){
        uv = abs(uv);
        uv -= 0.5;
        uv *= 1.1;
        uv *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }
    vec3 col = vec3(
     length(uv + vec2(.2, .1)),
     length(uv - vec2(.1, .1)),
     length(uv - vec2(.1, .2)));

    fragColor = vec4(col, 1.);
}
