#define I iResolution
#define PI 3.1415
#define TAU PI * 2.
#define rot( r ) mat2(cos(r), sin(r), -sin(r), cos(r) )
#define v( a ) clamp( a, 0., 1. )

float sdHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

float cc( vec2 u ){
    // ships
    float py = u.y;

    py = abs( pow( py, 2. ) );
    py -= .1;
    py *= 6. * ( u.y / 2. + .5 );
    py = abs( py ) - .01;

    return min(
        max(
            abs( u.x ) - .7 * py,
            abs( u.y ) - .3
        ),
        length( u + vec2( 0., -.1 ) ) - .19
    );
}

float texp( vec2 u ){
    float p;

    p = length( u ) - .4;

    p = max(
        p,
        -( length( u ) - .35 )
    );

    p = max(
        p,
        -( abs( u.y - .03 ) - .14 )
    );
    return p;
}

float texw( vec2 u ){
    float w = max(
        sin( atan( u.x, u.y + .29 ) * 3.5 - PI * .5 ),
        ( length( u ) - .28 )
    );

    vec2 cu = u * 4.;

    cu.y -= .3;
    cu.x = abs( cu.x ) - .5 * ( u.y / 2. + .5 );
    cu.y = abs( cu.y ) - .35;
    cu *= rot( PI * .1 );


    float cr = cc( cu * 1.1 );

    w = max(
        w,
        -cr
    );

    return w;
}

void updatePos( inout vec3 p ){
    p.xz *= rot( iTime );
    p.zy *= rot( iTime );
}

float hx( vec3 p ){

    updatePos( p );
    float py = p.y;

    py = abs( py ) - .5;
    py = clamp( py, -.27 , 1. );
    py = abs( py ) - .01;

    return max(
        sdHexagon( p.xz, 1.44 * py ),
        abs( p.y ) - .5
    );
}

float fcs( vec3 p ){
    updatePos( p );

    p.xz *= rot( PI * .34 );
    p.z += .18;

    return max(
        max( 
            abs( p.x ) - .21,
            abs( p.y ) - .21
        ),
        abs( p.z ) - .21
    );
}


float slavers( vec3 p ){

    p.xy *= rot( p.z * .8 );
    p.z += iTime * .5;
    p = mod( p, 1. ) - .5;
    p.xz *= rot( iTime );


    return max(
        sdHexagon( p.xy, .2 ),
        abs( p.z ) - .01
    );
}


vec3 lc( vec3 p ){
    p.xz *= rot( .5 );

    for( float i = 0.; i < 3.; i++ ){
        p.yz *= rot(  i );
        p.xy *= rot(  i );
        p.x = abs( p.x ) - i * .07;
        p.xz *= rot( i );
        p.z = abs( p.z ) - i * .07;
    }

    p.y -= .05;
    return p;
}


float df( vec3 p ){

    float d = 1.;

    d = min(
        d,
        slavers( p )
    );

    d = min(
        d,
        hx( p )
    );

    d = min(
        d,
        fcs( p )
    );

    return d;
}

vec3 l(in vec3 b) {
  vec2 a = vec2(1, -1) * .5773;
  return normalize(a.xyy * df(b + a.xyy * 5e-4) + a.yyx * df(b + a.yyx * 5e-4) +
                   a.yxy * df(b + a.yxy * 5e-4) + a.xxx * df(b + a.xxx * 5e-4));
}


void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = ( V * 2. - I.xy ) / I.y;


    vec3 c, p, o = vec3( 0., 0., -3. ), r = vec3( u * .25, 1. );
    float t, d, i, dp, e = sqrt( 1e-4 );

    for( ; i < 64.; i++ ){
        p = o + r * t,
        d = df( p ),
        dp = ( 1. - abs( d ) / e ),
        c += ( dp > 0. && df( p ) == hx(p) ) ? .01 * 64. / i * dp / t : 0.;

        if( dp > 0. && df( p ) == hx(p) ){
           c += lc( reflect( ( p ) , r ) ) * .3 * (2. - cos( vec3( .7, sin( p.x ), abs(r.z) ) ))
           * .35 * 64. / i * dp / t;
           c += vec3( 0.17, 0.78, 0.98 ) * .1 * 64. / i * dp / t;
        }


        if( dp > 0. && ( df( p ) == fcs( p ) ) ){
            vec3 pp = p;
            // yes the same as stereo yes
            updatePos( pp );

            pp.xz *= rot( PI * .34 );
            p.z += .18;

            float x = (mod( round(iTime* .5), 2. ) == 0. ) ? round( fract( iTime * 7. + 123.456 ) ) : 1.;

            c += max( -l( pp ).z, 0. ) * step( max( abs( pp.x ) - .2, abs( pp.y ) - .2 ), 0. ) * vec3( 0.16, 0.79, 0.97 ) * .01;
            c += vec3( max( -l( pp ).z, 0. ) * step( texw( pp.xy * 3. ), 0. ) ) * vec3( 0.97, 0.17, 0.31 ) * x;
            c += vec3( max( -l( pp ).z, 0. ) * step( texp( pp.xy * 3. ), 0. ) ) * vec3( 0.17, 0.97, 0.86 ) * x;
        }

        c += (  dp > 0. && ( df( p ) == slavers( p ) ) ) ? vec3( 0.17, 0.78, 0.98 ) * .05 * 64. / i * dp / t : vec3(0);



        t += max( abs( d )  * .66667, e );
    }




    // Output to screen
    U = vec4(c,1.0);
}
