void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixl coords
    vec2 uv = (fragCoord *2.-iResolution.xy)/iResolution.y;
    // color
    vec3 col = vec3(0.5+sin(iTime+uv.x+uv.y)*0.5,0.5+cos(0.7*iTime+uv.y)*0.5,sin(iTime*0.9));
    vec2 uvn = vec2(abs(uv.x), abs(uv.y));
    col.xyz += 0.1*sin(uvn.x*95.+iTime*100.03 + uvn.y*99.);
    col *= 2.-length(uv);
    // output
    fragColor = vec4(col,1.0);
}
