const float PI=3.1415926535;

float wave(in vec2 uv) {
    float disp = mod(iTime,2.0*PI);
    return 0.5*((sin( 5.0*(uv.x+uv.y) + 1.5*disp ))/2.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/(iResolution.xy - vec2(1.0,1.0));

    // Output to screen
    uv *=2.0;
    uv -= 1.0;

    uv.y *= iResolution.y/iResolution.x;

    fragColor.rgb=vec3(0.0,0.0,0.0)+wave(uv);

    if( uv.y < 0.21) {
        fragColor.rgb=vec3(1.0,1.0,1.0)+wave(uv)*1.5;
    }

    if( uv.y < -0.21 ) {
        fragColor.rgb=vec3(0.0,0.7+wave(uv),0.1);
    }

    if (uv.x+0.44<uv.y && uv.y<0.0) {
        fragColor.rgb=vec3(0.7+wave(uv),0.0,0.1);
    }

    if (uv.x+0.44<-uv.y && uv.y>=0.0) {
        fragColor.rgb=vec3(0.7+wave(uv),0.0,0.1);
    }
}
