#define PI 3.141596
vec3 a = vec3(0.4, 0.4, 0.4);
vec3 b = vec3(0.4, 0.4, 0.4);
vec3 c = vec3(1.0, 1.0, 1.0);
vec3 d = vec3(0.00, 0.33, 0.67);

// iq color mapper
vec3 colorMap(float t) {
	return (a + b * sin(2. * PI * (c * t + d)));
}

float Xor(float a, float b)
{
    return a*(1.-b) + b*(1.-a);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (1.*fragCoord-0.5*iResolution.xy)/iResolution.y;


    float a =.707;
    a=iTime;
    float s = sin(a);
    float c = cos(a);
    uv *= mat2(c, -s, s,c);
    uv*=15.;
    vec3 col =vec3(0.0);

    vec2 gv=fract(uv)-0.5;
    vec2 id=floor(uv);


    float m=0.;
    float t=iTime*2.;


    //vec3 color=vec3(0.4,0.6,0.75);
    vec3 color=vec3(0.0);

    for(float y=-1.; y<=1.; y++)
        for(float x=-1.; x<=1.; x++ )
        {
            vec2 offs=vec2(x,y);

            float d=length(gv-offs);
            float dist=length(id+offs)*0.3;

            float r=mix(0.3,1.5,sin(-t+dist)*0.5+0.5);
            //r=0.4;
           // m+=d;

           //m+=smoothstep(r,r*0.9,d);
            m=Xor(m,smoothstep(r*1.,r*0.9,d));
            color+=colorMap(dist)*m*0.5;
            //color
          //  color+=color*m/9.;
            //m+=smoothstep(0.6,0.5,d);
        }



   // col.rg =gv;
   col=color;
    //col+=m;
    //col*=colorMap(iTime);
    //col+=mod(m,2.);
    // Output to screen
    fragColor = vec4(col,1.0);
}
