#define R iResolution
#define T iTime

const float pi = 355. / 113.;
const float halfpi = 0.5 * pi;
const float phi = 0.5 * (1. + sqrt (5.));
const float phi0 = phi - 1.;
const float phi0i = 1. - phi0;

float psin (float t) {
    return 0.5 * (1. + sin (pi * t - halfpi));
}
vec2 psin (vec2 t) {
    return 0.5 * (1. + sin (pi * t - halfpi));
}

void mainImage (out vec4 C, in vec2 F) {
    float t = T;
    float r = R.x / R.y;
    vec2 uv = F / R.xy;
    vec2 uvn = (F - 0.5 * R.xy)
        / max (R.x, R.y)
        ;

    vec2 zuv = 33. * psin (0.6 * t) * uvn;
    float s = smoothstep (phi * r, -phi0 * r, length (psin (zuv)));

    vec2 tuv = mix (uvn, vec2 (s), psin (0.3 * t));
    vec3 color = vec3 (0.5, 0.0, 0.5);
    color =  (0. + s) * texture (iChannel0, 1.*tuv).rgb;
    color += (1. - s) * texture (iChannel1, 1.*tuv).rgb;

    C = vec4(color, 1.0);
}
