void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color1 = vec3(1.0, 0.0, 0.0);
    vec3 color2 = vec3(0.0, 1.0, 0.0);
    vec3 color3 = vec3(0.0, 0.0, 1.0);

    float marker_a = 0.3;
    float marker_b = 0.4;
    float marker_c = 0.8;
    vec3 col = texture(iChannel0, uv).rgb;
    float mask_1 = smoothstep(0.0, marker_a, col.r);
    float mask_2 =  smoothstep(marker_b, marker_a, col.g);
    float mask_3 = smoothstep(col.b, marker_c, 1.0);


    vec3 mixed_color = mix(color1, color2, mask_1);
    mixed_color = mix(mixed_color, color3, mask_3-mask_2);

   // mixed_color = mix(color1, color3, mask_1);



    // Output to screen
    fragColor = vec4(mixed_color,1.0);
}
