vec2 r2(float t){
    return vec2(cos(t) - sin(t), sin(t) + cos(t));
}

void mainImage(out vec4 f, in vec2 c){
    vec2 p = (c.xy - iResolution.xy * 0.5) / iResolution.xy;
    float r = length(p);
    p = p * r2((iTime * 3.0 + r * 10.0) * 0.3) / r;
    vec3 l = vec3(
        pow(
            cos((p.x * 10.0 * sin(r * 40.0)) * sin(p.y - vec3(0.65, 0.45, 0.45))),
            vec3(50.0)));
    f = vec4(l, 1.0);
}
